import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AccountService } from '../account.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  loginForm: FormGroup;
  errorMessage: string = '';

  constructor(
    private fb: FormBuilder,
    private accountService: AccountService,
    private router: Router
  ) {
    this.loginForm = this.fb.group({
      userName: ['', Validators.required],
      password: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.loginForm.invalid) {
      console.error('Form is invalid');
      return;
    }

    const loginData = {
      userName: this.loginForm.get('userName')?.value,
      password: this.loginForm.get('password')?.value,
    };

    this.accountService.login(loginData).subscribe({
      next: (response) => {
        console.log('Login successful:', response);
        if (response && response.token && response.role) {
          this.accountService.saveToken(response.token);
          this.accountService.setRole(response.role);

          // Kullanıcının rolüne göre yönlendirme
          if (response.role === 'Admin' || response.role === 'Client') {
            this.router.navigate(['/home']);  // Home sayfasına yönlendir
          } else {
            this.errorMessage = 'Unauthorized role';
          }
        } else {
          this.errorMessage = 'Invalid username or password';
        }
      },
      error: (error) => {
        console.error('Login failed:', error);
        this.errorMessage = 'An error occurred during login';
      }
    });
  }
}
<div class="background-image">
  <div class="overlay"></div>
  <div class="content">
    <div class="login-container">
      <h2>Login</h2>
      <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
        <div class="form-group my-2" style="display: flex ;justify-content: center;">
          <!-- <label for="userName">Username</label> -->
          <input id="userName" formControlName="userName" placeholder="İstifadəçi adınızı daxil edin" type="text" class="form-control w-50"  />
        </div>

        <div class="form-group my-2"  style="display: flex ;justify-content: center;">
          <!-- <label for="password">Password</label> -->
          <input id="password" formControlName="password" type="password" placeholder="Şifrənizi daxil edin"  class="form-control w-50" />
        </div>

        <button type="submit" [disabled]="loginForm.invalid" class="btn btn-primary mx-2">Login</button>
        <a routerLink="/account/register" class="btn btn-primary">Qeydiyyat</a>
        <div *ngIf="errorMessage" class="alert alert-danger mt-3">
          {{ errorMessage }}
        </div>
      </form>

    </div>

    <a routerLink="/account/resetpassword" class="my-4 resetpassword" >Parolu yenilə</a>
  </div>
</div>
