import { Injectable } from '@angular/core';
import { CanActivate, ActivatedRouteSnapshot, RouterStateSnapshot, Router } from '@angular/router';
import { Observable } from 'rxjs';
import { AccountService } from 'src/app/account/account.service';

@Injectable({
  providedIn: 'root'
})
export class AuthGuard implements CanActivate {

  constructor(private accountService: AccountService, private router: Router) { }

  canActivate(next: ActivatedRouteSnapshot, state: RouterStateSnapshot): boolean {
    const isLoggedIn = this.accountService.isLoggedIn();
    const role = this.accountService.getRole();

    // Eğer giriş yapılmışsa ve geçerli bir rota değilse, /home'a yönlendir
    if (isLoggedIn && (role === 'Admin' || role === 'Client')) {
      if (this.isInvalidRoute(state.url)) {
        this.router.navigate(['/home']); // Hatalı rotada home'a yönlendir
        return false;
      }
      return true;
    }

    // Eğer giriş yapılmamışsa ve geçerli bir rota değilse, /account/login'e yönlendir
    if (!isLoggedIn && this.isInvalidRoute(state.url)) {
      this.router.navigate(['/account/login']); // Hatalı rotada login'e yönlendir
      return false;
    }

    return false; // Varsayılan olarak erişimi engelle
  }

  // Hatalı rota kontrolü
  private isInvalidRoute(url: string): boolean {
    const validRoutes = ['/home', '/account/login', '/account/register', '/admin/dashboard'];
    return !validRoutes.includes(url); // Eğer URL geçerli rotalarda yoksa true döner
  }


}
