import { Component, OnInit } from '@angular/core';
import { BookService } from '../../book/book.service';
import { TypeService } from '../../type/type.service';
import { CategoryService } from '../../category/category.service';
import { AuthorService } from '../../author/author.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  menuItems = [
    { label: 'Kitablar', route: '/admin/book/book-list' },
    { label: 'Tipləri', route: '/admin/type/type-list' },
    { label: 'Müəllifləri', route: '/admin/author/author-list' },
    { label: 'Kateqoriyası', route: '/admin/category/category-list' },
  ];
  books: any[] = [];
  types: any[] = [];
  categories: any[] = [];
  authors: any[] = [];
  totalRecordsBook = 0;
  totalRecordsType = 0;
  totalRecordsAuthors = 0;
  totalRecordsCategories= 0;

  constructor( private bookService: BookService, private typeService: TypeService, private categoryService: CategoryService,private authorService: AuthorService) {}
  ngOnInit(): void {
    this.getBooks();
    this.getTypes();
    this.getAuthors();
    this.getCategories();
  }
 getBooks(): void { 
      this.bookService.getBooks().subscribe({
       next: (response: any) => {
          console.log('API Response:', response);
            this.books = response;
            this.totalRecordsBook = this.books.length;
        },
          error: error => console.log('Error fetching books:', error)
        })
  }
  getTypes(): void { 
    this.typeService.getTypes().subscribe({
     next: (response: any) => {
        console.log('API Response:', response);
          this.types = response;
          this.totalRecordsType = this.types.length;
      },
        error: error => console.log('Error fetching types:', error)
      })
  }
  getAuthors(): void { 
    this.authorService.getAuthors().subscribe({
     next: (response: any) => {
        console.log('API Response:', response);
          this.authors = response;
          this.totalRecordsAuthors = this.authors.length;
      },
        error: error => console.log('Error fetching types:', error)
      })
  }
  getCategories(): void { 
    this.categoryService.getCategories().subscribe({
     next: (response: any) => {
        console.log('API Response:', response);
          this.categories = response;
          this.totalRecordsCategories = this.categories.length;
      },
        error: error => console.log('Error fetching categories:', error)
      })
  }

}
