import { Component, OnInit } from '@angular/core';
import { SlideService } from '../slide.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-slide-edit',
  templateUrl: './slide-edit.component.html',
  styleUrls: ['./slide-edit.component.scss']
})
export class SlideEditComponent implements OnInit {
  slide: any = {
    id: 0,
    photo: null
  }
  constructor(private slideService: SlideService, private route: ActivatedRoute, private router: Router){}
  ngOnInit(): void {
    const slideId = this.route.snapshot.params['id'];
    if (slideId) {
      this.loadSlide();
    }
  }

  onFileChange(event: any, fileType: 'photo' ) {
    const file = event.target.files[0];
    if (fileType === 'photo') {
      this.slide.photo = file;
    } 
  }
  editSlide(): void {
    // const id = Number(this.route.snapshot.paramMap.get('id'));
    const formData = new FormData();
    if (this.slide.photo) {
      formData.append('Photo', this.slide.photo);
    }
    this.slideService.updateSlide(this.slide.id , formData).subscribe({
      next:(response: any) => {
        this.slide = response;
        this.router.navigate(['/admin/slide/slide-list']);

      },
      error: (error) => error.log("Fetch Error:", error)
    });
  }
  loadSlide() : void {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    if(id){
      this.slideService.getSlideById(id).subscribe({
        next: (response) => {
          console.log('API Response:', response); // Konsola yazdırın
          this.slide = response;

        },
        error: (error) =>{
          console.error('Error loading slide details:', error);
        }
      });
    }
  }

}
<div class="container mt-5">
  <h2 class="mb-4">Edit Book</h2>
  <form (ngSubmit)="updateBook()">
    <div class="mb-3">
      <label for="title" class="form-label">Title:</label>
      <input
        id="title"
        type="text"
        class="form-control"
        [(ngModel)]="book.title"
        name="title"
        required
      />
    </div>
    <div class="mb-3">
      <label for="description" class="form-label">Description:</label>
      <textarea
        id="description"
        class="form-control"
        [(ngModel)]="book.description"
        name="description"
        rows="3"
      ></textarea>
    </div>
    <div class="mb-3">
      <label for="createBook" class="form-label">Create Date:</label>
      <input
        id="createBook"
        type="date"
        class="form-control"
        [(ngModel)]="book.createBook"
        name="createBook"
        required
      />
    </div>
    <div class="mb-3">
      <label for="typeId" class="form-label">Type:</label>
      <select
        id="typeId"
        class="form-select"
        [(ngModel)]="book.typeId"
        name="typeId"
        required
      >
        <option *ngFor="let type of types" [value]="type.id">
          {{ type.typeName }}
        </option>
      </select>
    </div>
    <div class="mb-3">
      <label for="categoryId" class="form-label">Category:</label>
      <select
        id="categoryId"
        class="form-select"
        [(ngModel)]="book.categoryId"
        name="categoryId"
        required
      >
        <option *ngFor="let category of categories" [value]="category.id">
          {{ category.name }}
        </option>
      </select>
    </div>
    <div class="mb-3">
      <label for="authorId" class="form-label">Author:</label>
      <select
        id="authorId"
        class="form-select"
        [(ngModel)]="book.authorId"
        name="authorId"
        required
      >
        <option *ngFor="let author of authors" [value]="author.id">
          {{ author.fullName }}
        </option>
      </select>
    </div>
    <div class="my-2">
      <img *ngIf="book.photo" [src]="book.photo" alt="Book Photo" class="img-thumbnail my-2" style="max-width: 200px;" />
      <div class="w-100">
        <label for="photo" class="form-label">Photo</label>
        <input
          id="photo"
          type="file"
          class="form-control"
          (change)="onFileChange($event, 'photo')"
          placeholder="Choose a photo file"
        />
      </div>

    </div>
    <div class="mb-3">
      <label>Current Document:</label>
      <a class="mx-2" *ngIf="book.document" [href]="book.document" target="_blank">View Document</a>

      <div class="my-2">
        <label for="document" class="form-label">Document</label>
        <input
          id="document"
          type="file"
          class="form-control"
          (change)="onFileChange($event, 'document')"
          placeholder="Choose a document file"
        />
      </div>
    </div>

    <button type="submit" class="btn btn-primary">Update Book</button>
  </form>
</div>
