import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from 'src/environments/environment';
import { Router } from '@angular/router';
import { ReplaySubject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AccountService {
  private baseUrl = environment.apiUrl; // API URL
  private currentUserSource = new ReplaySubject<any | null>(1);
  currentUser$ = this.currentUserSource.asObservable();
  private role: string | null = null;

  constructor(private http: HttpClient, private router: Router) { }

  // Kullanıcı bilgilerini localStorage'a kaydeder
  saveToken(token: string): void {
    localStorage.setItem('token', token);
  }

  getToken(): string | null {
    return localStorage.getItem('token');
  }

  // Kullanıcı rolünü localStorage'a kaydeder
  setRole(role: string): void {
    this.role = role;
    localStorage.setItem('role', role);
  }

  // Giriş yapma işlemi
  login(credentials: any): Observable<any> {
    return this.http.post<any>(`${this.baseUrl}Account/login`, credentials); // login endpointi kullanılacak
  }

  // Giriş yapılıp yapılmadığını kontrol et
  isLoggedIn(): boolean {
    return !!localStorage.getItem('token');
  }
}
