import { Component, OnInit } from '@angular/core';
import { SlideService } from '../slide.service';
import { ActivatedRoute } from '@angular/router';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-slide-detail',
  templateUrl: './slide-detail.component.html',
  styleUrls: ['./slide-detail.component.scss']
})
export class SlideDetailComponent implements OnInit {
 slideId: number | null = null;
 slide: any;

 constructor(private slideService: SlideService,  private route: ActivatedRoute,){}

 ngOnInit(): void {
  const idParam = this.route.snapshot.paramMap.get('id');
}

loadSlide() : void {
  const id = Number(this.route.snapshot.paramMap.get('id'));
  if(id){
    this.slideService.getSlideById(id).subscribe({
      next: (response) => {
        console.log('API Response:', response); // Konsola yazdırın
        this.slide = response;
      },
      error: (error) =>{
        console.error('Error loading book details:', error);
      }
    });
  }
}


}
<div *ngIf="slide?.photo; else loading" class="container">
  <div class="row">
    <div class="col-md-6">
      <img 
        [src]="slide?.photo" 
        alt="Slide Image" 
        width="100" 
        height="60" 
        class="img-thumbnail" />
    </div>
  </div>
</div>

<ng-template #loading>
  <p>Loading slide details...</p>
</ng-template>
