import { Component } from '@angular/core';
import { AbstractControl, AsyncValidatorFn, FormBuilder, Validators } from '@angular/forms';
import { AccountService } from '../account.service';
import { Router } from '@angular/router';
import { debounceTime, finalize, map, switchMap, take } from 'rxjs';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
  errors: string[] | null = null;
  complexPassword = "(?=^.{6,10}$)(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&amp;*()_+}{\";:;'?/>,.<,])(?!.*\s).*$";

  registerForm = this.fb.group({
    displayName: ['', Validators.required],
    surname: ['', Validators.required],  // Yeni alan
    birthDate: ['', Validators.required],  // Yeni alan
    email: ['', [Validators.required, Validators.email], [this.validateEmailNotToken()]],
    password: ['', [Validators.required, Validators.pattern(this.complexPassword)]],
    confirmPassword: ['', [Validators.required, Validators.pattern(this.complexPassword)]]
  }, { validators: this.passwordMatchValidator });

  constructor(private fb: FormBuilder, private accountService: AccountService, private router: Router) {}

  // Register metodunu güncelle
  onSubmit() {
    if (this.registerForm.invalid) {
      return; // Eğer form geçersizse işlem yapma
    }

    // API çağrısını yapıyoruz
    this.accountService.register(this.registerForm.value).subscribe({
      next: () => {
        this.router.navigateByUrl('/shop'); // Başarılı kayıt sonrası shop sayfasına yönlendir
      },
      error: error => {
        // Hata durumunda error array'ini göster
        this.errors = error.errors;
      }
    });
  }

  // Password ve confirm password kontrolünü sağlar
  passwordMatchValidator(control: AbstractControl): { [key: string]: boolean } | null {
    const password = control.get('password')?.value;
    const confirmPassword = control.get('confirmPassword')?.value;
    return password && confirmPassword && password !== confirmPassword
      ? { 'passwordMismatch': true }
      : null;
  }

  // Email kontrolü async validatör fonksiyonu
  validateEmailNotToken(): AsyncValidatorFn {
    return (control: AbstractControl) => {
      return control.valueChanges.pipe(
        debounceTime(1000), // 1 saniyelik bir gecikme
        take(1),
        switchMap(() => {
          // Email mevcut mu diye kontrol et
          return this.accountService.checkEmailExists(control.value).pipe(
            map(result => result ? { emailExists: true } : null),
            finalize(() => control.markAsTouched()) // İşlem bittiğinde kontrolü işaretle
          );
        })
      );
    };
  }
}
