import { Component, OnInit } from '@angular/core';
import { CourseService } from './course.service';
import { Router } from '@angular/router';
import { AccountService } from 'src/app/account/account.service';
import { Course } from 'src/app/shared/models/course';

@Component({
  selector: 'app-course',
  templateUrl: './course.component.html',
  styleUrls: ['./course.component.scss']
})
export class CourseComponent implements OnInit {
  courses: any[] = [];
  role: string | null = null;
  constructor(private courseService: CourseService, private router: Router, private accountService: AccountService) {
    this.role = this.accountService.getRole()
  }


  ngOnInit(): void {
    this.loadCourses();
  }
  loadCourses() {
    this.courseService.getCourses().subscribe({
      next: (response: any) => {
        console.log('API Response Course:', response);
        this.courses = response;
      },
      error:  error => console.log('Error fetching course:', error)
    })
  }

}
<div class="container my-4">
    <div class="row  g-4">
        <h1 style="color:black; text-align:center">Hazırlıq Kursları</h1>
        <div class="col-md-4" *ngFor="let course of courses ">
            <a  *ngIf="role === 'Admin'" [routerLink]="['/admin/book/book-list']" [queryParams]="{ courseId: course.id }" style="text-decoration: none; border:none;">
                <div class="card  text-dark  h-100 d-flex flex-column justify-content-between card1" style=" background: linear-gradient(to right, #2d3b2f, #5a6e4e);">
