import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AccountService } from 'src/app/account/account.service';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.scss']
})
export class NavBarComponent implements OnInit {
  currentUser: any;
  role: string | null = null;

  constructor(public accountService: AccountService, private router: Router) {}

  ngOnInit(): void {
    this.accountService.currentUser$.subscribe(user => {
      this.currentUser = user;
      this.role = this.accountService.getRole(); // Role güncelleniyor
      console.log('Current User:', this.currentUser);  // Kullanıcı verisi
      console.log('Role:', this.role);  // Role değeri
    });
  }

}
<nav class="navbar navbar-expand-lg">
  <div class="container">
    <img class="mmulogo mx-2" src="assets/images/MMU Kontur2_040550.png" alt="">
    <img class="mmulogo" src="assets/images/HII-monoton-logo.png" alt="">
    <div class="collapse navbar-collapse mx-5 container" id="navbarNav">
      <h1 class="navbar-brand" href="#">Milli Müdafiə Universiteti</h1>
      <h1 class="navbar-brand" href="#">Hərbi İdarəetmə İnstitutu</h1>
    </div>
    <div>
      <a routerLink="/admin/dashboard" routerLinkActive="active" class="nav-link" style="width: 131px;">e-Kitabxana Giriş</a>
    </div>
  </div>
</nav>
