import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment';
import {HttpClient, HttpParams} from '@angular/common/http'
import { map, Observable } from 'rxjs';
import {Book} from 'src/app/shared/models/book'; 

@Injectable({
  providedIn: 'root'
})
export class BookService {
  baseUrl = environment.apiUrl;
  books: Book[] = [];
  constructor(private http: HttpClient) { }

  getBooks(): Observable<Book[]> {
    return this.http.get<any>(this.baseUrl + 'Book').pipe(
      map((response) => response.$values) // $values içindeki verileri alın
    );
  }

  addBook(book: Book): Observable<Book> {
    return this.http.post<Book>(this.baseUrl + 'Book/create/', book);
  }
  updateBook(id:number,book:Book): Observable<Book>{
    return this.http.put<Book>(`${this.baseUrl+ 'Book'}/${id}`, book);
  }
  deleteBook(id:number): Observable<any>{
    return this.http.delete<void>(`${this.baseUrl}/${id}`);
  }
  getBookById(id: number): Observable<Book>{
    return this.http.get<Book>(`${this.baseUrl + 'Book'}/${id}`);
  }
  getBook(params: any) {
    let httpParams = new HttpParams();

    for (let key in params) {
      if (params[key] !== undefined && params[key] !== null && params[key] !== '') {
        httpParams = httpParams.append(key, params[key]);
      }
    }

    console.log('HttpParams:', httpParams.toString()); // Kontrol edin
    return this.http.get<{ data: any[]; totalPages: number }>(
      this.baseUrl + 'Book/query',
      { params: httpParams }
    );
  }
}
