import { Component, OnInit } from '@angular/core';
import { AccountService } from '../account.service';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {

    loginForm = new FormGroup({
    displayName: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', Validators.required)
  })
  returnUrl: string;


  constructor(private accountService: AccountService, private router: Router, private activatedRoute: ActivatedRoute ) {
    this.returnUrl = this.activatedRoute.snapshot.queryParams['returnUrl'] || '/home'
  }

  onSubmit() {
    this.accountService.login(this.loginForm.value).subscribe({
      next: () => this.router.navigateByUrl(this.returnUrl)

    })
  }
}

    <div class="background-image"  >
        <div class="overlay"></div>
        <div class="content">

            <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">

                <div class="d-flex justify-content-center mt-5">
                    <div class="col-3">
                        <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
                            <div class="text-center mb-4">
                                <h1 class="mb-3">Login</h1>
                            </div>
                            <app-text-input [formControl]="loginForm.controls['displayName']" [label]="'Istifadəçi adı'"></app-text-input>
                            <app-text-input [formControl]="loginForm.controls['password']" [label]="'Password'" [type]="'password'"></app-text-input>

                            <div class="d-grid">
                                <button [disabled]="loginForm.invalid" class="btn btn-lg btn-primary mt-3" type="submit">Sign in</button>
                            </div>
                        </form>
                    </div>
                </div>


                <button type="submit" class="btn btn-primary mx-3"> Daxil ol</button>
                <a class="btn btn-primary my-2" [routerLink]="['/account/register']">Qeydiyyat</a>
            </form>

        </div>
    </div>
