import { Component, OnInit } from '@angular/core';
import { TypeService } from '../type.service';

@Component({
  selector: 'app-type-edit',
  templateUrl: './type-edit.component.html',
  styleUrls: ['./type-edit.component.scss']
})
export class TypeEditComponent implements OnInit {
type: any;
  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }
  constructor(private typeService: TypeService){}
  updateBook() {
    const formData = new FormData();
    formData.append('Name', this.type.name);

    this.typeService.updateBook(this.book.id, formData).subscribe({
      next: (response: any) => {
        console.log('Book updated successfully:', response);
        // Backend'den dönen güncellenmiş veriyi atayın
        this.book = response;
        this.router.navigate(['/admin/book/book-list']); // Başarılı güncelleme sonrası yönlendirme
      },
      error: (err) => {
        console.error('Error updating book:', err);
      }
    });
  }

}
