import { Component } from '@angular/core';

@Component({
  selector: 'app-change-role',
  templateUrl: './change-role.component.html',
  styleUrls: ['./change-role.component.scss']
})
export class ChangeRoleComponent  implements OnInit {
  changeRoleForm: FormGroup;
  availableRoles: string[] = [];
  errorMessage: string | null = null;

  constructor(private fb: FormBuilder, private userService: UserService) {
    this.changeRoleForm = this.fb.group({
      userId: ['', Validators.required],
      roles: this.fb.array([], Validators.required),
    });
  }

  ngOnInit(): void {
    this.userService.getAvailableRoles().subscribe({
      next: (roles) => (this.availableRoles = roles),
      error: (err) => console.error('Failed to load roles:', err),
    });
  }

  onSubmit(): void {
    if (this.changeRoleForm.invalid) {
      this.errorMessage = 'Form is invalid.';
      return;
    }

    const { userId, roles } = this.changeRoleForm.value;
    this.userService.changeRole(userId, roles).subscribe({
      next: (res) => {
        console.log('Role updated successfully:', res);
        this.errorMessage = null;
      },
      error: (err) => {
        console.error('Failed to update role:', err);
        this.errorMessage = 'Failed to update role.';
      },
    });
  }
}
