import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, ReplaySubject, catchError, map, of, throwError } from 'rxjs';
import { environment } from 'src/environments/environment';
import { User } from '../shared/models/user';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';
import { JsonPipe } from '@angular/common';

@Injectable({
  providedIn: 'root'
})
export class AccountService {

  baseUrl = environment.apiUrl;
  private currentUserSubject: BehaviorSubject<any>;
  public currentUser: Observable<any>;
  // private currentUserSource = new ReplaySubject<User | null>(1);
  // currentUser$ = this.currentUserSource.asObservable();

  constructor(private http: HttpClient, private router: Router) { 
    this.currentUserSubject = new BehaviorSubject<any>(JSON.parse(localStorage.getItem('user')!));
    this.currentUser = this.currentUserSubject.asObservable();
  }

  register(userData: any): Observable<any> {
    return this.http.post(`${this.baseUrl}Account/register`, userData);
  }



  // login(userData: any): Observable<any> {
  //   return this.http.post(`${this.baseUrl}Account/login`, userData);
  // }
  // login(loginData: { userName: string, password: string }): Observable<any> {
  //   return this.http.post(`${this.baseUrl}Account/login`, loginData);
  // }
  // login(loginData: any): Observable<any> {
  //   return this.http.post<any>(`${this.baseUrl}Account/login`, loginData).pipe(
  //     map(response => {
  //       if (response.token) {
  //         // Token alındıysa, başarılı login
  //         localStorage.setItem('token', response.token);
  //         return { success: true, ...response };
  //       } else {
  //         // Başarısız login
  //         return { success: false };
  //       }
  //     }),
  //     catchError(error => {
  //       return throwError(() => new Error('Login failed'));
  //     })
  //   );
  // }
  login(userName: string, password: string){
    return this.http.post(this.baseUrl+'' , {userName, password}).subscribe(response =>{
        localStorage.setItem('user', JSON.stringify(response));

        this.currentUser = this.currentUserSubject.asObservable();
    });
  }

  logout(): void {
    localStorage.removeItem('token');
    this.router.navigate(['/login']);
  }

  saveToken(token: string): void {
    localStorage.setItem('token', token);
  }

  getToken(): string | null {
    return localStorage.getItem('token');
  }

  isLoggedIn(): boolean {
    return !!localStorage.getItem('userToken'); 

  }
  getRole(): string {
    const currentuser = JSON.parse(localStorage.getItem('user')!);
    return currentuser?.role || '';
  }


}
