import { Component, OnInit } from '@angular/core';
import {Book} from 'src/app/shared/models/book'; 
import { BookService } from '../book.service';
import { ActivatedRoute } from '@angular/router';
import { BookParams } from 'src/app/shared/models/bookParams';
@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.scss']
})
export class BookListComponent implements OnInit{

  books: any[] = [];
  types: string[] = ['Fiction', 'Non-fiction', 'Science'];
  categories: string[] = ['Fantasy', 'Horror', 'Mystery'];
  authors: string[] = ['Author 1', 'Author 2', 'Author 3'];

  bookParams: any = {
    search: '',
    typeId: '',
    categoryId: '',
    authorId: '',
    pageNumber: 1,
    pageSize: 10,
  };

  constructor(private bookService: BookService, private route: ActivatedRoute) {}
    ngOnInit(): void {
      this.getBooks();
      // this.getBook();
    }


    getBooks(): void {
      this.bookService.getBooks(this.bookParams).subscribe(
        (response) => {
          console.log('API Response:', response.books);
          this.books = response.books;
          // Sayfa sayısı vs. gerekiyorsa burada alınabilir.
        },
        (error) => {
          console.error('Error fetching books:', error);
        }
      );
    }

    onSearch(search: string): void {
      this.bookParams.search = search;
      this.bookParams.pageNumber = 1;
      this.getBooks();
    }

    applyFilters(): void {
      this.bookParams.pageNumber = 1;
      this.getBooks();
    }

    changePage(pageNumber: number): void {
      if (pageNumber > 0) {
        this.bookParams.pageNumber = pageNumber;
        this.getBooks();
      }
    }

  }
<input
  #inputRef
  type="text"
  placeholder="Search..."
  (input)="onSearch(inputRef.value)"
/>

<select class="form-control" [(ngModel)]="bookParams.typeId" (change)="applyFilters()">
  <option value="">All Types</option>
  <option *ngFor="let type of types" [value]="type">{{ type }}</option>
</select>

<select class="form-control" [(ngModel)]="bookParams.categoryId" (change)="applyFilters()">
  <option value="">All Categories</option>
  <option *ngFor="let category of categories" [value]="category">{{ category }}</option>
</select>

<select class="form-control" [(ngModel)]="bookParams.authorId" (change)="applyFilters()">
  <option value="">All Authors</option>
  <option *ngFor="let author of authors" [value]="author">{{ author }}</option>
</select>

<table class="table table-borderless my-5">
  <thead>
    <tr>
      <th scope="col">Title</th>
      <th scope="col">Foto</th>
      <th scope="col">Description</th>
      <th scope="col">Type</th>
      <th scope="col">Author</th>
      <th scope="col">Category</th>
      <th scope="col">Operations</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let book of books">
      <td>{{ book.title }}</td>
      <td>
        <img [src]="book.photo || 'assets/default-image.jpg'" style="width: 100px; height: 100px;" alt="" />
      </td>
      <td>{{ book.description }}</td>
      <td>{{ book.bookType }}</td>
      <td>{{ book.bookAuthor }}</td>
      <td>{{ book.bookCategory }}</td>
      <td>
        <a [routerLink]="['/admin/book/book-detail', book.id]">Detail</a>
        <a [routerLink]="['/admin/book/book-delete', book.id]" class="btn btn-danger">Delete</a>
      </td>
    </tr>
  </tbody>
</table>

<div class="pagination">
  <button (click)="changePage(bookParams.pageNumber - 1)" [disabled]="bookParams.pageNumber === 1">Previous</button>
  <span>Page {{ bookParams.pageNumber }}</span>
  <button (click)="changePage(bookParams.pageNumber + 1)" [disabled]="books.length < bookParams.pageSize">Next</button>
</div>
