import { Component } from '@angular/core';
import { AccountService } from '../account.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
  model: any = {};

  constructor(private accountService: AccountService, private router: Router) {}

  register() {
    this.accountService.register(this.model).subscribe({
      next: () => this.router.navigateByUrl('/account/login'),
      error: error => console.log(error)
    });
  }
}
<div class="background-image" style=" background: url('./Assets/images/WhatsApp\ Image\ 2024-12-06\ at\ 11.08.58_14dc8878.jpg') no-repeat center center/cover;">
    <div class="overlay"></div>
    <div class="content">

        <form (ngSubmit)="register()">
            <div class="form-group  my-3">
                <input class="form-control"  type="text" [(ngModel)]="model.displayName" name="displayName"   placeholder="İstifadəçi adı daxil edin:">
            </div>
            <div class="form-group  my-3">
                <input class="form-control"  type="text" [(ngModel)]="model.name" name="name"    placeholder="Adınızı daxil edin:">
            </div>
            <div class="form-group  my-3">
                <input class="form-control"  type="text" [(ngModel)]="model.surname" name="surname" placeholder="Soyadınızı daxil edin:">
            </div>
            <div class="form-group  my-3">
                <input class="form-control"  type="date" type="text" [(ngModel)]="model.surname" name="surname"  placeholder="Doğum tarixinizi daxil edin:">
            </div>
            <div class="form-group  my-3">
            </div>
            <div class=" form-group my-3">
                <input class="form-control" type="password"  placeholder="Şifrə daxil edin:">
            </div>
            <div class=" form-group my-3">
                <input class="form-control" type="password"  placeholder="Eyni şifrəni daxil edin:">
            </div>


            <button type="submit" class="btn btn-primary"> Qeydiyyatdan keç</button>
        </form>
    </div>
</div>
