import { Component, OnInit } from '@angular/core';
import { TypeService } from '../type.service';
import { Router } from '@angular/router';
import { Type } from 'src/app/shared/models/type';

@Component({
  selector: 'app-type-edit',
  templateUrl: './type-edit.component.html',
  styleUrls: ['./type-edit.component.scss']
})
export class TypeEditComponent implements OnInit {
  type: any = {
    id: 0,
    name:'',
  };

  constructor(private typeService: TypeService, private router: Router){}

  ngOnInit(): void {
    this.updateType();

    const typeId = this.router.snapshot.params['id'];
    if (typeId) {
      this.loadType();
    }
  }
  loadType() {
    const id = Number(this.router.snapshot.paramMap.get('id'));
    if (id) {
      this.bookService.getBookById(id).subscribe({
        next: (response: any) => {
          console.log('API Response:', response);
          this.book = response;

          // Tarih formatını düzelt
          if (this.book.createBook) {
            this.book.createBook = this.book.createBook.split('T')[0];
          }

          // Backend'den gelen bookType, bookCategory ve bookAuthor ile eşleştirme
          if (this.book.bookType) {
            this.book.typeId = this.types.find(type => type.typeName === this.book.bookType)?.id || 0;
          }
          if (this.book.bookCategory) {
            this.book.categoryId = this.categories.find(category => category.name === this.book.bookCategory)?.id || 0;
          }
          if (this.book.bookAuthor) {
            this.book.authorId = this.authors.find(author => author.fullName === this.book.bookAuthor)?.id || 0;
          }
        },
        error: (error) => {
          console.error('Error loading book details:', error);
        }
      });
    }


  }

  updateType() {
    const formData = new FormData();
    formData.append('typeName', this.type.name);

    this.typeService.updateType(this.type.id, formData).subscribe({
      next: (response: any) => {
        console.log('Type updated successfully:', response);
        // Backend'den dönen güncellenmiş veriyi atayın
        this.type = response;
        this.router.navigate(['/admin/tyep/type-list']); // Başarılı güncelleme sonrası yönlendirme
      },
      error: (error) => {
        console.error('Error updating type:', error);
      }
    });
  }
}
