import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { UserService } from '../user.service';

@Component({
  selector: 'app-user-add',
  templateUrl: './user-add.component.html',
  styleUrls: ['./user-add.component.scss'],
})
export class UserAddComponent implements OnInit {
  registerForm: FormGroup;
  errorMessage: string | null = null;
  availableRoles: string[] = []; // Rolleri backend'den alacağız.

  constructor(
    private fb: FormBuilder,
    private userService: UserService,
    private router: Router
  ) {
    this.registerForm = this.fb.group({
      userName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      name: ['', Validators.required],
      surname: ['', Validators.required],
      birthDate: ['', Validators.required],
      password: [
        '',
        [
          Validators.required,
          Validators.minLength(6),
          Validators.pattern(
            '(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)(?=.*[!@#$%^&*()_+}{":;\'?,.<>]).*'
          ),
        ],
      ],
      confirmPassword: ['', [Validators.required, Validators.pattern('')]],
      roles: [[], Validators.required],
    });
  }

  ngOnInit(): void {}
  onRoleChange(event: Event): void {
    const selectElement = event.target as HTMLSelectElement;
    const selectedRoles = Array.from(selectElement.selectedOptions).map(option => option.value);
    this.registerForm.patchValue({ roles: selectedRoles });
  }
  onSubmit(): void {
    if (this.registerForm.invalid) return;

    this.userServicethis.registerForm.value).subscribe({
      next: () => {
        alert('User registered successfully');
      },
      error: (err) => {
        this.errorMessage = err.error?.message || 'An error occurred';
      },
    });
  }
}

    const payload = {
      RegisterDto: {
        UserName: this.registerForm.get('userName')?.value,
        Email: this.registerForm.get('email')?.value,
        Name: this.registerForm.get('name')?.value,
        Surname: this.registerForm.get('surname')?.value,
        BirthDate: new Date(this.registerForm.get('birthDate')?.value).toISOString(),
        Password: this.registerForm.get('password')?.value,
        ConfirmPassword: this.registerForm.get('confirmPassword')?.value,
      },
      RoleDto: {
        Roles: this.registerForm.get('roles')?.value,
      },
    };

    this.userService.getRegisterRole(payload).subscribe({
      next: (response) => {
        console.log('Registration successful:', response);
        this.router.navigate(['admin/user/user-list']);
      },
      error: (error) => {
        console.error('Registration failed:', error);
        this.errorMessage = error.error || 'An error occurred while registering.';
      },
    });
  }
}
<div class="user-add-container">
  <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
    <div>
      <label for="userName">User Name</label>
      <input id="userName" formControlName="userName" type="text" />
    </div>

    <div>
      <label for="email">Email</label>
      <input id="email" formControlName="email" type="email" />
    </div>

    <div>
      <label for="name">Name</label>
      <input id="name" formControlName="name" type="text" />
    </div>

    <div>
      <label for="surname">Surname</label>
      <input id="surname" formControlName="surname" type="text" />
    </div>

    <div>
      <label for="birthDate">Birth Date</label>
      <input id="birthDate" formControlName="birthDate" type="date" />
    </div>

      <div>
        <label for="roles">Roles:</label>
        <select id="roles" multiple (change)="onRoleChange($event)">
          <option *ngFor="let role of availableRoles" [value]="role">{{ role }}</option>
        </select>
      </div>

      <div>
        <label for="password">Password:</label>
        <input id="password" formControlName="password" type="password" />
      </div>

      <div>
        <label for="confirmPassword">Confirm Password:</label>
        <input id="confirmPassword" formControlName="confirmPassword" type="password" />
      </div>

      <button type="submit" [disabled]="registerForm.invalid">Register</button>
      <div *ngIf="errorMessage" class="error-message">{{ errorMessage }}</div>
    </form>


</div>
