import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { HomeService } from '../home.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Home } from 'src/app/shared/models/home';

@Component({
  selector: 'app-home-edit',
  templateUrl: './home-edit.component.html',
  styleUrls: ['./home-edit.component.scss']
})
export class HomeEditComponent implements OnInit {
  updateForm: FormGroup;
  selectedFiles: File[] = [];
  homeId!: number;
  homes: any[] = [];
  homePhotos: any[] = [];
  home: Home | undefined; // Home objesi tanımlandı

  constructor(
    private fb: FormBuilder,
    private homeService: HomeService,
    private route: ActivatedRoute,
    private router: Router
  ) {
    this.updateForm = this.fb.group({
      title: [''],
      description: ['']
    });

    this.homeId = this.route.snapshot.params['id'];
  }

  ngOnInit(): void {
    this.loadHomeDetails();
  }

  // Veriyi API'den alıp formu doldur
  loadHomeDetails(): void {
    this.homeService.gethomeById(this.homeId).subscribe({
      next: (home) => {
        this.updateForm.patchValue({
          title: home.title,
          description: home.description,
        });
        // Mevcut fotoğrafları yükle
        this.home = home;

        // Fotoğrafları al ve homePhotos dizisine ata
        if (home.photo?.$values) {
          this.homePhotos = home.photo.$values.map((url: string) => ({
            photo: url.startsWith('http://') ? url.replace('http://', 'https://') : url,
          }));
        } else {
          this.homePhotos = []; // Fotoğraflar yoksa boş bırak
        }
      },
      error: (err) => {
        console.error('Error fetching home details:', err);
        alert('An error occurred while loading the home details.');
      }
    });
  }loadHome() {
    const id = Number(this.route.snapshot.paramMap.get('id'));

    if (id) {
      this.homeService.gethomeById(id).subscribe({
        next: (response: any) => {
          console.log('API Response home:', response);

          // Ana bilgiyi ata
          this.home = response;

          // Fotoğrafları al ve homePhotos dizisine ata
          if (response.photo?.$values) {
            this.homePhotos = response.photo.$values.map((url: string) => ({
              photo: url.startsWith('http://') ? url.replace('http://', 'https://') : url,
            }));
          } else {
            this.homePhotos = []; // Fotoğraflar yoksa boş bırak
          }
        },
        error: (error) => {
          console.error('Error loading home details:', error);
        },
      });
    }
  }

  // Fotoğraf seçimi
  onFileSelected(event: any) {
    this.selectedFiles = Array.from(event.target.files);
  }

  // Fotoğraf silme
  deletePhoto(id: number) {
    if (confirm('Are you sure you want to delete this photo?')) {
      this.homeService.deletePhoto(id).subscribe({
        next: () => {
          // Listeden silinen fotoğrafı çıkar
          this.homePhotos = this.homePhotos.filter((photo) => photo.id !== id);
          alert('Photo deleted successfully!');
        },
        error: (err) => {
          console.error('Error deleting photo:', err);
          alert('An error occurred while deleting the photo.');
        }
      });
    }
  }

  // Form submit işlemi
  onSubmit() {
    const formData = new FormData();
    formData.append('title', this.updateForm.get('title')?.value || '');
    formData.append('description', this.updateForm.get('description')?.value || '');

    for (const file of this.selectedFiles) {
      formData.append('photos', file);
    }

    this.homeService.updateHome(this.homeId, formData).subscribe({
      next: (response) => {
        console.log('Update response:', response);
        alert('Home updated successfully!');
        this.router.navigate(['/admin/home/home-list']);
      },
      error: (err) => {
        console.error('Error updating home:', err);
        alert('An error occurred while updating the home.');
      }
    });
  }
}
