import { Component, OnInit } from '@angular/core';
import { BookService } from '../book.service';
import { TypeService } from '../../type/type.service';
import { AuthorService } from '../../author/author.service';
import { Book } from 'src/app/shared/models/book';

@Component({
  selector: 'app-book-add',
  templateUrl: './book-add.component.html',
  styleUrls: ['./book-add.component.scss']
})
export class BookAddComponent implements OnInit{
  types: any[] = [];
  categories: any[] = [];
  authors: any[] = [];
  book: any = {
    id: 0,
    title: '',
    description: '',
    createBook: new Date().toISOString(),
    photo: '',
    document: '',
    typeId: null,
    authorId: null,
    categoryId: null,
    bookType: undefined,
    bookAuthor: undefined,
    bookCategory: undefined,
  };
  constructor(private bookService: BookService, private typeService: TypeService,private authorService: AuthorService){}

  ngOnInit(): void {
    this.loadTypes();
    this.loadCategories();
    this.loadAuthors();
    this.addBook();
  }

    loadTypes() {
      this.bookService.getTypes().subscribe({
       next: (response: any) => {
          console.log('API Response type:', response);
            this.types = response;
        },
          error: error => console.log('Error fetching books:', error)
        })
    }
    loadAuthors() {
      this.bookService.getAuthors().subscribe({
       next: (response: any) => {
          console.log('API Response author:', response);
            this.authors = response;
        },
          error: error => console.log('Error fetching books:', error)
        })
    }
    loadCategories() {
      this.bookService.getCategories().subscribe({
        next: ( response : any) => {
          console.log('API Response category:', response);
          this.categories = response;
        },
        error: error=> console.log('Error fetching books:', error)

      })
    }

    addBook() {
      if (!this.book.title || !this.book.typeId || !this.book.categoryId || !this.book.authorId) {
        alert('Bütün sahələri doldurmaq tələb olunur!');
        return;
      }

      this.bookService.addBook(this.book).subscribe({
        next:( response: any) => {
          alert('Book added successfully!');
          console.log('Book added:', response);
          this.resetForm();
        },
        error: (error: any) => {
          console.error('Error adding book:', error);
        }
      });
    }

    onFileSelected(event: any, field: 'photo' | 'document') {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = () => {
          // File içeriğini base64 olarak alıp ilgili alanına koyar.
          if (field === 'photo') {
            this.book.photo = reader.result as string;
          } else if (field === 'document') {
            this.book.document = reader.result as string;
          }
        };
        reader.readAsDataURL(file); // Dosyayı base64 formatında okur.
      }
    }



    resetForm() {
      this.book = {
        id: 0,
        title: '',
        description: '',
        createBook: new Date().toISOString(),
        photo: '',
        document: '',
        typeId: null,
        authorId: null,
        categoryId: null,
        bookType: undefined,
        bookAuthor: undefined,
        bookCategory: undefined,
      };
    }

}
