import { Component, OnInit } from '@angular/core';
import {Book} from 'src/app/shared/models/book'; 
import { BookService } from '../book.service';
import { ActivatedRoute } from '@angular/router';
import { BookParams } from 'src/app/shared/models/bookParams';
import { TypeService } from '../../type/type.service';
import { AuthorService } from '../../author/author.service';
@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.scss']
})
export class BookListComponent implements OnInit{

    books: any[] = [];
    types: string[] = [];
    categories: string[] = [];
    authors: string[] = [];
    totalRecords: number = 0;

    bookParams: BookParams = {
      pageNumber: 1,
      pageSize: 10,
      search: '',
      typeId: '',
      categoryId: '',
      authorId: ''
    };

    constructor(
      private bookService: BookService,
      private typeService: TypeService,
      // private categoryService: CategoryService,
      private authorService: AuthorService
    ) {}

    ngOnInit(): void {
      this.getBooks();
      // this.getTypes();
      // this.getCategories();
      // this.getAuthors();
    }

    getBooks(): void {
      this.bookService.getBooks(this.bookParams).subscribe({
        next: (response) => {
          console.log('API Response:', response); // Konsola yazdırın
          this.books = response;
          this.totalRecords = response.totalCount;
        },
        error: (error) => {
          console.error('Error fetching books:', error); // Hataları yazdırın
        }
      });
    }
    onSearch(search: string): void {
      this.bookParams.search = search;
      this.bookParams.pageNumber = 1;
      this.getBooks();
    }

    applyFilters(): void {
      this.bookParams.pageNumber = 1;
      this.getBooks();
    }
    changePage(pageNumber: number): void {
      if (pageNumber > 0) {
        this.bookParams.pageNumber = pageNumber;
        this.getBooks();
      }
    }

    // getBooks(): void {
    //   this.bookService.getBooks(this.bookParams).subscribe(
    //     (response) => {
    //       console.log('API Response:', response);

    //       // Gelen verideki $values dizisini alıyoruz
    //       if (response && response.$values) {
    //         this.books = response.$values;
    //       } else {
    //         this.books = [];
    //       }
    //     },
    //     (error) => {
    //       console.error('Error fetching books:', error);
    //     }
    //   );
    // }


    // getTypes(): void {
    //   this.typeService.getTypes().subscribe((types) => (this.types = types));
    // }

    // getCategories(): void {
    //   this.categoryService.getCategories().subscribe((categories) => (this.categories = categories));
    // }

    // getAuthors(): void {
    //   this.authorService.getAuthors().subscribe((authors) => (this.authors = authors));
    // }

    // onSearch(search: string): void {
    //   this.bookParams.search = search;
    //   this.bookParams.pageNumber = 1;
    //   this.getBooks();
    // }

    // applyFilters(): void {
    //   this.bookParams.pageNumber = 1;
    //   this.getBooks();
    // }

    // changePage(pageNumber: number): void {
    //   if (pageNumber > 0) {
    //     this.bookParams.pageNumber = pageNumber;
    //     this.getBooks();
    //   }
    // }
  }
<input
  type="text"
  placeholder="Search..."
  (input)="onSearch($event.target.value)"
/>

<select [(ngModel)]="bookParams.typeId" (change)="applyFilters()">
  <option value="">All Types</option>
  <option *ngFor="let type of types" [value]="type">{{ type }}</option>
</select>

<select [(ngModel)]="bookParams.categoryId" (change)="applyFilters()">
  <option value="">All Categories</option>
  <option *ngFor="let category of categories" [value]="category">{{ category }}</option>
</select>

<select [(ngModel)]="bookParams.authorId" (change)="applyFilters()">
  <option value="">All Authors</option>
  <option *ngFor="let author of authors" [value]="author">{{ author }}</option>
</select>

<table class="table table-borderless my-5">
    <thead>
        <tr>

            <th scope="col">Title</th>
            <th scope="col">Foto </th>
            <th scope="col">Description</th>
            <th scope="col">Type</th>
            <th scope="col">Author</th>
            <th scope="col">Category</th>
            <th scope="col">Operations</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let book of books">

            <td scope="row">{{book.title}}</td>
            <td>
                <img src="{{book.photo}}" style="width: 100px; height:100px;" alt="">
            </td>
            <td>{{book.description}}</td>
            <td>{{book.bookType}}</td>
            <td>{{book.bookAuthor}}</td>
            <td>{{book.bookCategory}}</td>
            <td>
                <a [routerLink]="['/admin/book/book-detail', book.id]" ><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" class="bi bi-eye " style="color:black;" viewBox="0 0 16 16">
                    <path d="M16 8s-3-5.5-8-5.5S0 8 0 8s3 5.5 8 5.5S16 8 16 8M1.173 8a13 13 0 0 1 1.66-2.043C4.12 4.668 5.88 3.5 8 3.5s3.879 1.168 5.168 2.457A13 13 0 0 1 14.828 8q-.086.13-.195.288c-.335.48-.83 1.12-1.465 1.755C11.879 11.332 10.119 12.5 8 12.5s-3.879-1.168-5.168-2.457A13 13 0 0 1 1.172 8z"/>
                    <path d="M8 5.5a2.5 2.5 0 1 0 0 5 2.5 2.5 0 0 0 0-5M4.5 8a3.5 3.5 0 1 1 7 0 3.5 3.5 0 0 1-7 0"/>
                  </svg></a>
                  <a [routerLink]="['/admin/book/book-delete', book.id]" class="btn btn-danger"> Delete</a>
                  <!-- <button data-toggle="modal" data-target="#exampleModal" class=" mx-1 btn btn-danger" type="submit" value="Sil" onclick="getBook()" ><i class="fa fa-trash" aria-hidden="true"></i></button> -->
                <!-- <button (click)="deleteBook(book.id!)" class="btn btn-danger">Delete</button> -->
            </td>
        </tr>
    </tbody>
</table>

<div class="pagination">
  <button (click)="changePage(bookParams.pageNumber - 1)" [disabled]="bookParams.pageNumber === 1">Previous</button>
  <span>Page {{ bookParams.pageNumber }}</span>
  <button (click)="changePage(bookParams.pageNumber + 1)" [disabled]="books.length < bookParams.pageSize">Next</button>
</div>

