import { AbstractControl, AsyncValidatorFn, FormBuilder, Validators } from '@angular/forms';
import { AccountService } from '../account.service';
import { Router } from '@angular/router';
import { debounceTime, finalize, map, switchMap, take } from 'rxjs';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent{
  errors: string[] | null = null;
  constructor(private fb: FormBuilder, private accountService: AccountService, private router: Router) {}

  complexPassword= "(?=^.{6,}$)(?=.*\\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*()_+}{\":;'?/&.,<>]).*$"

  registerForm : any = this.fb.group({
    username: ['', Validators.required],
    name: ['', Validators.required],
    surname: ['', Validators.required],
    birthdate: ['', Validators.required],  // Date form control
    email: ['', [Validators.required, Validators.email], [this.validateEmailNotToken()]],
    password: ['', [Validators.required, Validators.pattern(this.complexPassword)]],
    confirmpassword: ['', [Validators.required, Validators.pattern(this.complexPassword)]]
  });


  onSubmit() {
    this.accountService.register(this.registerForm.value).subscribe({

      next: (response: any ) => {
        console.log('API response register:', response);
        this.router.navigateByUrl('account/login')
      },
      error: error => this.errors = error.errors
    });
  }

  validateEmailNotToken(): AsyncValidatorFn{
    return (control: AbstractControl) =>{
      return control.valueChanges.pipe(
        debounceTime(1000),
        take(1),
        switchMap(() => {
          return this.accountService.checkEmailExists(control.value).pipe(
            map(result => result ? {emailExists: true} : null),
            finalize(() => control.markAsTouched())
          )
        })
      )
    }
  }
}
