import { Injectable } from '@angular/core';
import { Observable, ReplaySubject, catchError, map, of, throwError } from 'rxjs';
import { environment } from 'src/environments/environment';
import { User } from '../shared/models/user';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class AccountService {

  baseUrl = environment.apiUrl;
  private currentUserSource = new ReplaySubject<User | null>(1);
  currentUser$ = this.currentUserSource.asObservable();

  constructor(private http: HttpClient, private router: Router) { }

  register(userData: any): Observable<any> {
    return this.http.post(`${this.baseUrl}Account/register`, userData);
  }

  // login(userData: any): Observable<any> {
  //   return this.http.post(`${this.baseUrl}Account/login`, userData);
  // }
  // login(loginData: { userName: string, password: string }): Observable<any> {
  //   return this.http.post(`${this.baseUrl}Account/login`, loginData);
  // }
  login(loginData: any): Observable<any> {
    return this.http.post<any>(`${this.baseUrl}Account/login`, loginData).pipe(
      map(response => {
        if (response.token) {
          // Token alındıysa, başarılı login
          localStorage.setItem('token', response.token);
          return { success: true, ...response };
        } else {
          // Başarısız login
          return { success: false };
        }
      }),
      catchError(error => {
        return throwError(() => new Error('Login failed'));
      })
    );
  }

  logout(): void {
    localStorage.removeItem('token');
    localStorage.removeItem('role'); // Rolü kaldır
    this.router.navigate(['/login']);
  }

  saveToken(token: string): void {
    localStorage.setItem('token', token);
  }

  getToken(): string | null {
    return localStorage.getItem('token');
  }

  isLoggedIn(): boolean {
    const token = localStorage.getItem('token');
    return !!token;
  }

  getRole(): string | null {
    if (!this.role) {
      this.role = localStorage.getItem('role'); // Rol bilgisi token ile depolanmış olmalı
    }
    return this.role;
  }

  setRole(role: string): void {
    this.role = role;
    localStorage.setItem('role', role); // Rol bilgisini localStorage'de saklayın
  }


}
