import { Component } from '@angular/core';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.scss']
})
export class AdminComponent {
  menuItems = [
    { label: 'Kitablar', route: '/admin/book/book-list' },
    { label: 'Tipləri', route: '/admin/type/type-list' },
    { label: 'Müəllifləri', route: '/admin/author/author-list' },
  ];
}
