import { Component } from '@angular/core';
import { UserService } from '../user.service';

@Component({
  selector: 'app-user-add',
  templateUrl: './user-add.component.html',
  styleUrls: ['./user-add.component.scss']
})
export class UserAddComponent {
  registerDto = {
    name: '',
    surname: '',
    email: '',
    birthDate: '',
    password: '',
    confirmPassword: '',
    userName: '',
  };

  roleDto = {
    roles: [] as string[],
  };
  availableRoles = ['Admin', 'Client'];

  constructor(private userService: UserService, private router: Router) {}

  onSubmit() {
    const registerWithRoleDto = {
      registerDto: this.registerDto,
      roleDto: this.roleDto,
    };

    this.userService.getRegisterRole(registerWithRoleDto).subscribe({
      next: (response) => {
        console.log('User registered successfully:', response);
        alert('User registered successfully!');
        this.router.navigate(['admin/user/user-list']); // Login sayfasına yönlendirme
      },
      error: (error) => {
        console.error('Error registering user:', error);
        alert('Failed to register user.');
      },
    });
  }

  toggleRole(role: string, event: any) {
    if (event.target.checked) {
      this.roleDto.roles.push(role);
    } else {
      this.roleDto.roles = this.roleDto.roles.filter((r) => r !== role);
    }
  }
}
