import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-change-role',
  templateUrl: './change-role.component.html',
  styleUrls: ['./change-role.component.scss']
})
export class ChangeRoleComponent implements OnInit {
  changeRoleForm: FormGroup = this.fb.group({
    userId: ['', Validators.required],
    roles: [[], Validators.required]
  });

  availableRoles: string[] = ['Admin', 'Client'];  // Rolleri burada belirliyoruz
  errorMessage: string | null = null;

  constructor(private fb: FormBuilder) { }

  ngOnInit(): void {
    this.changeRoleForm = this.fb.group({
      userId: ['', Validators.required],
      roles: [[], Validators.required]
    });
  }

  onSubmit(): void {
    if (this.changeRoleForm.invalid) {
      console.error('Form is invalid');
      return;
    }

    const formData = this.changeRoleForm.value;
    console.log('Submitted form data:', formData);

    // API ile role değişikliği işlemi yapılabilir
  } 
}
