import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AccountService } from 'src/app/account/account.service';
import { HomeService } from 'src/app/admin/home/home.service';
import { SlideService } from 'src/app/admin/slide/slide.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit{
  slides: any[] = [];
  slide: any;
 role: string  | null = null;
 home: any;
 homePhotos: any[] = [];
 groupedSlides: any[] = [];
  constructor(private router: Router, private route:ActivatedRoute, private accountService: AccountService, private homeService: HomeService, private slideService: SlideService) {
    this.role = this.accountService.getRole();
  }
  ngOnInit(): void {
    this.loadDefaultHome();
    this.loadSlides();
    this.groupSlides(3);
  }

  loadDefaultHome(): void {
    this.homeService.getDefaultHome().subscribe({
      next: (response: any) => {
        console.log('Default Home:', response);

        // Ana veri
        this.home = response;

        // Fotoğrafları işleme
        if (response.photos?.$values && Array.isArray(response.photos.$values)) {
          // Fotoğrafları homePhotos dizisine ekle
          this.homePhotos = response.photos.$values.map((photo: any) => ({
            photo: photo.photoUrl.startsWith('http://') ? photo.photoUrl.replace('http://', 'https://') : photo.photoUrl,
          }));
          console.log('Home Photos:', this.homePhotos);
        } else {
          this.homePhotos = []; // Fotoğraflar yoksa boş bırak
        }
      },
      error: (error) => {
        console.error('Error loading default home:', error);
      }
    });
  }
  groupSlides(groupSize: number) {
    for (let i = 0; i < this.slides.length; i += groupSize) {
      this.groupedSlides.push(this.slides.slice(i, i + groupSize));
    }
  }
    loadSlides(): void {
      this.slideService.getAllSlides().subscribe({
        next: (response) => {
          console.log('Slides loaded:', response);
          this.slides = response; 
          this.groupSlides(3);
        },
        error: (error) => {
          console.error('Error loading slides:', error);
        }
      });
    }


}
<section class="bgc" *ngIf="role === 'Admin' || role === 'Client' ">
  <div *ngIf="home" class="container my-5">
    <div class="row">
        <div class="col-md-6 mt-5">
          <h2 style="color:#fff; text-align: left; font-size: 27px;" class="mb-4">
            {{home.title}}
          </h2>
          <p *ngIf="home.description" style="color:#fff; font-size: 21px; font-style: arial;">
           {{home.description}}
          </p>
        </div>
        <div class="col-md-6">
          <div id="carouselExampleIndicators" class="carousel slide" data-bs-ride="carousel">
            <div class="carousel-inner">
              <div *ngFor="let photo of homePhotos; let i = index"  [ngClass]="{'active': i === 0}" class="carousel-item " >
                <img class="d-flex w-100  imageslide" style="margin: 0 auto;"  [src]="photo.photo" alt="Home Image {{ i + 1 }}">
              </div>
              <!-- <div class="carousel-item ">
                <img class="d-flex w-100  imageslide" style="margin: 0 auto;" src="/assets/images/E-kitabxana.jpg" alt="First slide">
              </div>
              <div class="carousel-item">
                <img class="d-block w-100 imageslide" src="/assets/images/e-kitabxana(1).jpg" alt="Second slide">
              </div>
              <div class="carousel-item">
                <img class="d-block w-100 imageslide" src="/assets/images/e-kitabxana(2).jpg" alt="Third slide">
              </div>
              <div class="carousel-item">
                <img class="d-block w-100 imageslide" src="/assets/images/kitabxana.jpg" alt="Third slide">
              </div>
              <div class="carousel-item">
                <img class="d-block w-100 imageslide" src="/assets/images/H.Aliyev.jpg" alt="Third slide">
              </div> -->
            </div>
            <div *ngIf="homePhotos.length > 1">
              <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="prev">
                <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                <span class="visually-hidden">Previous</span>
              </button>
              <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="next">
                <span class="carousel-control-next-icon" aria-hidden="true"></span>
                <span class="visually-hidden">Next</span>
              </button>
            </div>
          </div>
      </div>
    </div>
  </div>
</section>
<section class="bgc " *ngIf="role === 'Admin' || role === 'Client'">
  <div class="container-fluid mb-5 mt-1">
    <div id="multiItemCarousel" class="carousel slide " style="display: flex; justify-content: center;" data-bs-ride="carousel">

      <!-- Indicators -->
      <div class="carousel-indicators">
        <button *ngFor="let group of groupedSlides; let i = index"
                type="button"
                [attr.data-bs-target]="'#multiItemCarousel'"
                [attr.data-bs-slide-to]="i"
                [class.active]="i === 0"
                [attr.aria-current]="i === 0 ? 'true' : null"
                [attr.aria-label]="'Slide ' + (i + 1)">
        </button>
      </div>

      <!-- Carousel Items -->
      <div class="carousel-inner">
        <div class="carousel-item" *ngFor="let group of groupedSlides; let i = index" [class.active]="i === 0">
          <div class="row">
            <div class="col-md-4" *ngFor="let slide of group">
              <img *ngIf="slide.photo" [src]="slide.photo" class="d-block imageslide2" [alt]="'Image ' + slide.photo">
            </div>
          </div>
        </div>
      </div>

      <!-- Optional Controls -->
       <div >
        <button class="carousel-control-prev" type="button" data-bs-target="#multiItemCarousel" data-bs-slide="prev">
          <span class="carousel-control-prev-icon" aria-hidden="true"></span>
          <span class="visually-hidden">Previous</span>
        </button>
        <button class="carousel-control-next" type="button" data-bs-target="#multiItemCarousel" data-bs-slide="next">
          <span class="carousel-control-next-icon" aria-hidden="true"></span>
          <span class="visually-hidden">Next</span>
        </button>
       </div>

    </div>
  </div>
</section>
