import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment';
import {HttpClient, HttpParams} from '@angular/common/http'
import { map, Observable } from 'rxjs';
import {Book, PaginatedResponse} from 'src/app/shared/models/book'; 
import { BookParams } from 'src/app/shared/models/bookParams';

@Injectable({
  providedIn: 'root'
})
export class BookService {
  baseUrl = environment.apiUrl;
  books:  Book[] = [];
  constructor(private http: HttpClient) { }

  getBooks(params: any): Observable<any> {
    return this.http.get(`${this.apiBaseUrl}/Book/query`, { params });
  }

  getCategories(): Observable<any[]> {
    return this.http.get<any[]>(`${this.apiBaseUrl}/Category`);
  }

  getTypes(): Observable<any[]> {
    return this.http.get<any[]>(`${this.apiBaseUrl}/Type`);
  }

  getAuthors(): Observable<any[]> {
    return this.http.get<any[]>(`${this.apiBaseUrl}/Author`);
  }
  addBook(book: Book): Observable<Book> {
    return this.http.post<Book>(this.baseUrl + 'Book/create/', book);
  }
  updateBook(id:number,book:Book): Observable<Book>{
    return this.http.put<Book>(`${this.baseUrl+ 'Book'}/${id}`, book);
  }
  deleteBook(id:number): Observable<any>{
    return this.http.delete<void>(`${this.baseUrl}/Book/${id}`);
  }
  getBookById(id: number): Observable<Book>{
    return this.http.get<Book>(`${this.baseUrl + 'Book'}/${id}`);
  }

