import { Component } from '@angular/core';
import { AccountService } from '../account/account.service';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.scss']
})
export class AdminComponent {
  menuItems = [
    { label: 'Kitablar', route: '/admin/book/book-list' },
    { label: 'Tipləri', route: '/admin/type/type-list' },
    { label: 'Müəllifləri', route: '/admin/author/author-list' },
    { label: 'Kateqoriyası', route: '/admin/category/category-list' },
    { label: 'Header parametrlər', route: '/admin/home/home-list' },
  ];
  constructor(public accountService: AccountService) {}

  logout() {
    this.accountService.logout();
  }
}
