import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AccountService } from '../account.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm: FormGroup;
  errorMessage: string = '';

  constructor(
    private fb: FormBuilder,
    private accountService: AccountService,
    private router: Router
  ) {
    this.loginForm = this.fb.group({
      userName: ['', Validators.required],
      password: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.loginForm.invalid) {
      console.error('Form is invalid');
      return;
    }

    const loginData = {
      userName: this.loginForm.get('userName')?.value,
      password: this.loginForm.get('password')?.value,
    };

    this.accountService.login(loginData).subscribe({
      next: (response) => {
        console.log('Login successful:', response);
        if (response && response.token && response.role) {
          this.accountService.saveToken(response.token);
          this.accountService.setRole(response.role);

          // Kullanıcının rolüne göre yönlendirme
          if (response.role === 'Admin' || response.role === 'Client') {
            this.router.navigate(['/home']);  // Home sayfasına yönlendir
          } else {
            this.errorMessage = 'Unauthorized role';
          }
        } else {
          this.errorMessage = 'Invalid username or password';
        }
      },
      error: (error) => {
        console.error('Login failed:', error);
        this.errorMessage = 'An error occurred during login';
      }
    });
  }
}
<div class="background-image">
  <div class="overlay"></div>
  <div class="content">
    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
      <h2 class="text-white mb-4">Login</h2>

      <div class="mb-3">
        <div class="form-floating d-flex justify-content-center">
          <input
            class="form-control w-50"
            id="userName"
            formControlName="userName"
            type="text"
            placeholder="Username"
          />
        </div>
        <div *ngIf="loginForm.get('userName')?.invalid && loginForm.get('userName')?.touched" class="text-danger small mt-1">
          Username is required.
        </div>
      </div>

      <div class="mb-3">
        <div class="form-floating d-flex justify-content-center">
          <input
            class="form-control w-50"
            id="password"
            formControlName="password"
            type="password"
            placeholder="Password"
          />
        </div>
        <div *ngIf="loginForm.get('password')?.invalid && loginForm.get('password')?.touched" class="text-danger small mt-1">
          Password is required.
        </div>
      </div>

      <!-- Error mesajı -->
      <div *ngIf="errorMessage" class="text-danger text-center mt-2">
        {{ errorMessage }}
      </div>

      <!-- Login butonu -->
      <button class="btn btn-primary mx-3" type="submit" [disabled]="loginForm.invalid">Login</button>

      <!-- Kayıt ol linki -->
      <a class="btn btn-primary" [routerLink]="['/account/register']">Qeydiyyat</a>
    </form>
  </div>
</div>
