import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { UserService } from '../user.service';

@Component({
  selector: 'app-user-add',
  templateUrl: './user-add.component.html',
  styleUrls: ['./user-add.component.scss'],
})
export class UserAddComponent implements OnInit {
  registerForm!: FormGroup;
  errorMessage: string | null = null;
  availableRoles: string[] = []; // Backend'den alınacak roller

  constructor(
    private fb: FormBuilder,
    private userService: UserService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.initializeForm();
    this.loadRoles();
  }

  initializeForm(): void {
    this.registerForm = this.fb.group({
      userName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      name: ['', Validators.required],
      surname: ['', Validators.required],
      birthDate: ['', Validators.required],
      password: [
        '',
        [
          Validators.required,
          Validators.minLength(6),
          Validators.pattern(
            '(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)(?=.*[!@#$%^&*()_+}{":;\'?,.<>]).*'
          ),
        ],
      ],
      confirmPassword: [
        '',
        [
          Validators.required,
          Validators.pattern(''),
        ],
      ],
      roles: [[], Validators.required],
    });
  }

  loadRoles(): void {
    this.userService.getAvailableRoles().subscribe({
      next: (roles) => {
        if (Array.isArray(roles)) {
          this.availableRoles = roles;
        } else {
          console.error('Received roles are not an array:', roles);
          this.errorMessage = 'Received roles are not in the correct format.';
        }
      },
      error: (err) => {
        console.error('Failed to load roles:', err);
        this.errorMessage = 'Failed to load available roles.';
      },
    });
  }


  onRoleChange(event: Event): void {
    const selectElement = event.target as HTMLSelectElement;
    const selectedRoles = Array.from(selectElement.selectedOptions).map(
      (option) => option.value
    );
    this.registerForm.patchValue({ roles: selectedRoles });
  }

  onSubmit(): void {
    if (this.registerForm.invalid) {
      console.error('Form is invalid');
      return;
    }

    const payload = {
      RegisterDto: {
        UserName: this.registerForm.get('userName')?.value,
        Email: this.registerForm.get('email')?.value,
        Name: this.registerForm.get('name')?.value,
        Surname: this.registerForm.get('surname')?.value,
        BirthDate: new Date(
          this.registerForm.get('birthDate')?.value
        ).toISOString(),
        Password: this.registerForm.get('password')?.value,
        ConfirmPassword: this.registerForm.get('confirmPassword')?.value,
      },
      RoleDto: {
        Roles: this.registerForm.get('roles')?.value,
      },
    };

    this.userService.getRegisterRole(payload).subscribe({
      next: () => {
        console.log('User registered successfully');
        this.router.navigate(['admin/user/user-list']);
      },
      error: (error) => {
        console.error('Registration failed:', error);
        this.errorMessage =
          error.error?.message || 'An error occurred during registration.';
      },
    });
  }
}
<form [formGroup]="registerForm" (ngSubmit)="onSubmit()" class="container mt-5">
  <h2 class="text-center mb-4">User Registration</h2>

  <div class="form-group mb-3">
    <label for="userName">Username:</label>
    <input
      id="userName"
      formControlName="userName"
      type="text"
      class="form-control"
      placeholder="Enter username"
    />
  </div>

  <div class="form-group mb-3">
    <label for="email">Email:</label>
    <input
      id="email"
      formControlName="email"
      type="email"
      class="form-control"
      placeholder="Enter email"
    />
  </div>

  <div class="form-group mb-3">
    <label for="name">Name:</label>
    <input
      id="name"
      formControlName="name"
      type="text"
      class="form-control"
      placeholder="Enter name"
    />
  </div>

  <div class="form-group mb-3">
    <label for="surname">Surname:</label>
    <input
      id="surname"
      formControlName="surname"
      type="text"
      class="form-control"
      placeholder="Enter surname"
    />
  </div>

  <div class="form-group mb-3">
    <label for="birthDate">Birth Date:</label>
    <input
      id="birthDate"
      formControlName="birthDate"
      type="date"
      class="form-control"
    />
  </div>

  <div class="form-group mb-3">
    <label for="password">Password:</label>
    <input
      id="password"
      formControlName="password"
      type="password"
      class="form-control"
      placeholder="Enter password"
    />
  </div>

  <div class="form-group mb-3">
    <label for="confirmPassword">Confirm Password:</label>
    <input
      id="confirmPassword"
      formControlName="confirmPassword"
      type="password"
      class="form-control"
      placeholder="Confirm password"
    />
  </div>

  <div class="form-group mb-3">
    <label for="roles">Roles:</label>
    <select
      id="roles"
      formControlName="roles"
      multiple
      class="form-control"
      (change)="onRoleChange($event)"
    >
      <option *ngFor="let role of availableRoles" [value]="role">{{ role }}</option>
    </select>
  </div>

  <div *ngIf="errorMessage" class="alert alert-danger mt-3">{{ errorMessage }}</div>

  <button type="submit" class="btn btn-primary btn-block" [disabled]="registerForm.invalid">Register</button>
</form>
