import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AccountService } from 'src/app/account/account.service';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.scss']
})
export class NavBarComponent implements OnInit {
  currentUser: any;
  role: string | null = null;

  constructor(public accountService: AccountService, private router: Router) {}

  ngOnInit(): void {
    // Kullanıcı verisini almak için subscribe ediyoruz
    this.accountService.currentUser$.subscribe(user => {
      this.currentUser = user;
      this.role = this.accountService.getRole(); // Role güncelleniyor
      console.log('Current User:', this.currentUser);  // Doğru veriyi alıp almadığınızı kontrol edin
    });
    console.log('Current URL:', window.location.href);  // Navbar'ın hangi sayfada gösterildiğini kontrol edin
  }
}
