import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { HomeService } from '../home.service';

@Component({
  selector: 'app-home-edit',
  templateUrl: './home-edit.component.html',
  styleUrls: ['./home-edit.component.scss']
})
export class HomeEditComponent implements OnInit {
  updateForm: FormGroup;
  selectedFiles: File[] = [];
  homeId!: number;
  home: any | undefined;
  homePhotos: any[] = [];
  photoToDelete: any = null;
  constructor(
    private fb: FormBuilder,
    private homeService: HomeService,
    private route: ActivatedRoute,
    private router: Router,
  ) {
    this.updateForm = this.fb.group({
      title: [''],
      description: [''],
    });

    this.homeId = this.route.snapshot.params['id'];
  }

  ngOnInit(): void {
    this.loadHomeDetails();
  }

  loadHomeDetails(): void {
    this.homeService.gethomeById(this.homeId).subscribe({
      next: (response: any) => {
        this.home = response;

        // Formu güncelle
        this.updateForm.patchValue({
          title: response.title,
          description: response.description,
        });

        // Fotoğrafları al ve homePhotos dizisine ata
        if (response.photos?.$values && Array.isArray(response.photos.$values)) {
          this.homePhotos = response.photos.$values.map((photo: any) => ({
            id: photo.id,
            photo: photo.photoUrl.startsWith('http://') ? photo.photoUrl.replace('http://', 'https://') : photo.photoUrl,
          }));
        } else {
          this.homePhotos = [];
        }
      },
      error: (err) => {
        console.error('Error fetching home details:', err);
        alert('An error occurred while loading the home details.');
      }
    });
  }

  // Fotoğraf silme modal'ını açma
  openDeleteModal(photo: any): void {
    this.photoToDelete = photo;

    const deleteModal = document.getElementById('deletePhotoModal');
    if (deleteModal) {
      const modal = new (window as any).bootstrap.Modal(deleteModal);
      modal.show();
    }
  }

  // Fotoğrafı silme
  confirmDelete(): void {
    if (this.photoToDelete) {
      // Fotoğraf silme API çağrısı
      this.homeService.deletePhoto(this.photoToDelete.id).subscribe({
        next: (response: any) => {
          console.log('Delete response:', response); // Fotoğraf silindi

          // Fotoğrafı homePhotos dizisinden çıkartma
          this.homePhotos = this.homePhotos.filter((photo) => photo.id !== this.photoToDelete.id);

          // Modalı kapatma
          const deleteModal = document.getElementById('deletePhotoModal');
          if (deleteModal) {
            const modalInstance = (window as any).bootstrap.Modal.getInstance(deleteModal);
            modalInstance?.hide();
          }

          this.photoToDelete = null;
        },
        error: (err) => {
          console.error('Error deleting photo:', err);
        }
      });
    }
  }

  onFileSelected(event: any): void {
    this.selectedFiles = Array.from(event.target.files);
  }

  onSubmit(): void {
    const formData = new FormData();
    formData.append('title', this.updateForm.get('title')?.value || '');
    formData.append('description', this.updateForm.get('description')?.value || '');

    // Silinen fotoğrafları formData'ya ekleyin
    formData.append('photosToDelete', JSON.stringify(this.photosToDelete));

    // Yeni fotoğrafları ekleyin
    for (const file of this.selectedFiles) {
      formData.append('photos', file);
    }

    this.homeService.updateHome(this.homeId, formData).subscribe({
      next: (response) => {
        console.log('Update response:', response);
        alert('Home updated successfully!');
        this.router.navigate(['/admin/home/home-list']);
      },
      error: (err) => {
        console.error('Error updating home:', err);
        alert('An error occurred while updating the home.');
      }
    });
  }
}

<div class="container">
    <h2>Update Home</h2>
    <form [formGroup]="updateForm" (ngSubmit)="onSubmit()">
      <div class="mb-3">
        <label for="title" class="form-label">Title</label>
        <input type="text" id="title" formControlName="title" class="form-control" />
      </div>
  
      <div class="mb-3">
        <label for="description" class="form-label">Description</label>
        <textarea id="description" formControlName="description" class="form-control"></textarea>
      </div>
  
      <div class="mb-3">
        <label for="photos" class="form-label">Photos</label>
        <input type="file" id="photos" (change)="onFileSelected($event)" class="form-control" multiple />
      </div>
  
      <!-- Mevcut Fotoğraflar -->
   <!-- home-edit.component.html -->

<div *ngIf="homePhotos.length > 0" class="mb-3">
  <label class="form-label">Existing Photos</label>
  <div id="carouselExampleIndicators" class="carousel slide" data-bs-ride="carousel">
    <div class="carousel-inner">
      <div *ngFor="let homePhoto of homePhotos; let i = index" class="carousel-item" [ngClass]="{'active': i === 0}">
        <img class="d-block w-100 imageslide" style="height: 400px;" [src]="homePhoto.photo" alt="Home Image {{ i + 1 }}">
        <!-- Fotoğrafı Silme Butonu -->
        <button class="btn btn-danger" (click)="openDeleteModal(photo)">Delete</button>
      </div>
    </div>
    <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="prev">
      <span class="carousel-control-prev-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Previous</span>
    </button>
    <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="next">
      <span class="carousel-control-next-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Next</span>
    </button>
  </div>
</div>
      <button type="submit" class="btn btn-primary">Update</button>
    </form>
  </div>
  
<!-- Delete Photo Modal -->
<div class="modal" tabindex="-1" id="deletePhotoModal">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Delete Photo</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <p>Are you sure you want to delete this photo?</p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Cancel</button>
        <button type="button" class="btn btn-danger" (click)="confirmDelete()">Delete</button>
      </div>
    </div>
  </div>
</div>
