import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { UserService } from '../user.service';

@Component({
  selector: 'app-user-add',
  templateUrl: './user-add.component.html',
  styleUrls: ['./user-add.component.scss'],
})
export class UserAddComponent implements OnInit {
  registerForm: FormGroup;
  availableRoles: string[] = [];
  errorMessage: string | null = null;

  constructor(
    private fb: FormBuilder,
    private userService: UserService,
    private router: Router
  ) {
    this.registerForm = this.fb.group({
      userName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      name: ['', Validators.required],
      surname: ['', Validators.required],
      birthDate: ['', Validators.required],
      password: [
        '',
        [
          Validators.required,
          Validators.minLength(6),
          Validators.pattern('(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)(?=.*[!@#$%^&*()_+}{":;\',.<>?/]).*'),
        ],
      ],
      confirmPassword: ['', Validators.required],
      role: ['', Validators.required], // Role seçimi için alan
    });
  }

  ngOnInit(): void {
    this.loadRoles();
  }

  loadRoles(): void {
    this.userService.getAvailableRoles().subscribe({
      next: (roles) => {
        this.availableRoles = roles;
      },
      error: (error) => {
        console.error('Failed to load roles:', error);
        this.errorMessage = 'Roles could not be loaded.';
      },
    });
  }

  onSubmit(): void {
    if (this.registerForm.invalid) {
      console.error('Form is invalid');
      return;
    }

    const formData = {
      RegisterDto: {
        UserName: this.registerForm.get('userName')?.value,
        Email: this.registerForm.get('email')?.value,
        Name: this.registerForm.get('name')?.value,
        Surname: this.registerForm.get('surname')?.value,
        BirthDate: this.registerForm.get('birthDate')?.value,
        Password: this.registerForm.get('password')?.value,
        ConfirmPassword: this.registerForm.get('confirmPassword')?.value,
      },
      RoleDto: {
        Roles: [this.registerForm.get('role')?.value], // Seçilen role buraya ekleniyor
      },
    };

    this.userService.getRegisterRole(formData).subscribe({
      next: (response) => {
        console.log('Registration successful:', response);
        this.router.navigate(['/admin/user/user-list']);
      },
      error: (error) => {
        console.error('Registration failed:', error);
        this.errorMessage = error.error?.message || 'Registration failed.';
      },
    });
  }
}
<div class="background-image">
  <div class="overlay"></div>
  <div class="content">
    <form [formGroup]="registerForm" (ngSubmit)="onSubmit()" class="needs-validation">
      <h2 class="text-white mb-4">Qeydiyyat</h2>

      <!-- User Name -->
      <div class="mb-3">
        <div class="form-floating d-flex justify-content-center">
          <input
            class="form-control w-50"
            id="userName"
            formControlName="userName"
            type="text"
            placeholder="User Name"
          />
        </div>
        <div *ngIf="registerForm.get('userName')?.invalid && registerForm.get('userName')?.touched" class="text-danger small mt-1">
          User Name is required.
        </div>
      </div>

      <!-- Email -->
      <div class="mb-3">
        <div class="form-floating d-flex justify-content-center">
          <input
            class="form-control w-50"
            id="email"
            formControlName="email"
            type="email"
            placeholder="Email"
          />
        </div>
        <div *ngIf="registerForm.get('email')?.invalid && registerForm.get('email')?.touched" class="text-danger small mt-1">
          Valid email is required.
        </div>
      </div>

      <!-- Name -->
      <div class="mb-3">
        <div class="form-floating d-flex justify-content-center">
          <input
            class="form-control w-50"
            id="name"
            formControlName="name"
            type="text"
            placeholder="Name"
          />
        </div>
        <div *ngIf="registerForm.get('name')?.invalid && registerForm.get('name')?.touched" class="text-danger small mt-1">
          Name is required.
        </div>
      </div>

      <!-- Surname -->
      <div class="mb-3">
        <div class="form-floating d-flex justify-content-center">
          <input
            class="form-control w-50"
            id="surname"
            formControlName="surname"
            type="text"
            placeholder="Surname"
          />
        </div>
        <div *ngIf="registerForm.get('surname')?.invalid && registerForm.get('surname')?.touched" class="text-danger small mt-1">
          Surname is required.
        </div>
      </div>

      <!-- Birth Date -->
      <div class="mb-3">
        <div class="form-floating d-flex justify-content-center">
          <input
            class="form-control w-50"
            id="birthDate"
            formControlName="birthDate"
            type="date"
            placeholder="Birth Date"
          />
        </div>
        <div *ngIf="registerForm.get('birthDate')?.invalid && registerForm.get('birthDate')?.touched" class="text-danger small mt-1">
          Birth Date is required.
        </div>
      </div>

      <!-- Password -->
      <div class="mb-3">
        <div class="form-floating d-flex justify-content-center">
          <input
            class="form-control w-50"
            id="password"
            formControlName="password"
            type="password"
            placeholder="Password"
          />
        </div>
        <div *ngIf="registerForm.get('password')?.invalid && registerForm.get('password')?.touched" class="text-danger small mt-1">
          Password must be at least 6 characters and include one uppercase letter, one number, and one special character.
        </div>
      </div>

      <!-- Confirm Password -->
      <div class="mb-3">
        <div class="form-floating d-flex justify-content-center">
          <input
            class="form-control w-50"
            id="confirmPassword"
            formControlName="confirmPassword"
            type="password"
            placeholder="Confirm Password"
          />
        </div>
        <div *ngIf="registerForm.get('confirmPassword')?.invalid && registerForm.get('confirmPassword')?.touched" class="text-danger small mt-1">
          Confirmation Password must match the Password.
        </div>
      </div>



      <!-- Submit Button -->
      <div class="d-flex justify-content-center">
        <button type="submit" class="btn btn-primary btn-lg w-50" [disabled]="registerForm.invalid">
          Register
        </button>
      </div>
    </form>
  </div>
</div>
