import { Component, OnInit } from '@angular/core';
import {Book} from 'src/app/shared/models/book'; 
import { BookService } from '../book.service';
import { ActivatedRoute } from '@angular/router';
@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.scss']
})
export class BookListComponent implements OnInit{

  books: any[] = [];
  types:string[] = [];
  categories:string[]=[];
  authors:string[]=[];
  book: any | null = null;

  selectedType: string = '';
  selectedCategory: string = '';
  selectedAuthor: string = '';
  searchTerm: string='';
  currentPage: number = 1;
  pageSize: number = 10;
  totalPages: number = 1

  constructor(private bookService: BookService, private route: ActivatedRoute) {}
    ngOnInit(): void {
      this.loadBooks();
      // this.getBooks();
      // this.getBook();
    }
    loadBooks(): void {
      const params: any = {
        type: this.selectedType,
        category: this.selectedCategory,
        author: this.selectedAuthor,
        search: this.searchTerm,
        currentPage: this.currentPage,
        pageSize: this.pageSize,
      };

      this.bookService.getBook(params).subscribe(
        (response) => {
          console.log('API Response:', response); // Konsola yazdırın

          // Gelen veri yapısına uygun atamalar
          this.books = response.data || [];
          this.types = response.types || [];
          this.categories = response.categories || [];
          this.authors = response.authors || []; // Kitap listesi "data" alanında
          this.totalPages = response.totalPages || 1; // Toplam sayfa sayısı
        },
        (error) => {
          console.error('Error fetching books:', error);
        }
      );
    }
    applyFilters(): void{
      this.currentPage = 1;
      this.loadBooks();
    }
    onSearch(event: any): void{
      this.searchTerm = event.target.value; 
      this.currentPage = 1;
      this.loadBooks();
    }
    changePage(page:number): void{
      if(page>= 1 && page <= this.totalPages){
        this.currentPage = page;
        this.loadBooks();
      }
    }
    // getBooks() {
    //   this.bookService.getBooks().subscribe({
    //     next: (response: any) => {
    //       console.log('API Response:', response); // Konsola yazdırın
    //       this.books = response;
    //     },
    //     error: (error) => {
    //       console.error('Error fetching books:', error); // Hataları yazdırın
    //     }
    //   });
    // }
    // getBook() : void {
    //   const id = Number(this.route.snapshot.paramMap.get('id'));
    //   if(id){
    //     this.bookService.getBookById(id).subscribe({

    //       next: (response) => {
    //         console.log('API Response:', response); // Konsola yazdırın
    //         this.book = response;
    //       },
    //       error: (error) =>{
    //         console.error('Error loading book details:', error);
    //       }
    //     });
    //   }
    // }
    // DeleteBook(id: number) {
    //   this.bookService.deleteBook(id).subscribe(() => {
    //     this.getBook();
    //   });
    // }

}
<input
  type="text"
  [(ngModel)]="searchTerm"
  (input)="onSearch($event)"
  placeholder="Search books..."
  class="form-control"
/>

<select
  class="form-control"
  [(ngModel)]="selectedType"
  (change)="applyFilters()"
>
  <option value="">All Types</option>
  <option *ngFor="let type of types" [value]="type">{{ type }}</option>
</select>

<select
  class="form-control"
  [(ngModel)]="selectedCategory"
  (change)="applyFilters()"
>
  <option value="">All Categories</option>
  <option *ngFor="let category of categories" [value]="category">{{ category }}</option>
</select>

<select
  class="form-control"
  [(ngModel)]="selectedAuthor"
  (change)="applyFilters()"
>
  <option value="">All Authors</option>
  <option *ngFor="let author of authors" [value]="author">{{ author }}</option>
</select>
