import { Component, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { SlideService } from '../slide.service';
import { ActivatedRoute, Router } from '@angular/router';
import { AccountService } from 'src/app/account/account.service';
import { environment } from 'src/environments/environment';

@Component({
  selector: 'app-slide-list',
  templateUrl: './slide-list.component.html',
  styleUrls: ['./slide-list.component.scss']
})
export class SlideListComponent implements OnInit {

  slides: any[] = [];
  slide: any;
  role: string | null = null;
  constructor(private fb: FormBuilder, private slideService: SlideService, private router: Router, private route: ActivatedRoute, private accountService: AccountService) {
      this.role = this.accountService.getRole();
    }
  ngOnInit(): void {
    this.loadSlide();
  }
  loadSlide() {
    this.slideService.getAllSlides().subscribe({
      next: (response: any) => {
        console.log('Slides API Response:', response);
        // Fotoğraf URL'sini tam olarak oluşturuyoruz
        this.slides = response.map((slide: any) => ({
          ...slide,
          photoUrl: slide.photo ? `${environment.apiUrl}wwwroot/images/${slide.photo}` : null
        }));
      },
      error: (error) => console.error('Error fetching slides:', error)
    });
  }



}
