import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { HomeComponent } from './home/home/home.component';
import { DashboardComponent } from './admin/dashboard/dashboard/dashboard.component';
import { AdminComponent } from './admin/admin.component';
import { LoginComponent } from './account/login/login.component';
import { RegisterComponent } from './account/register/register.component';
import { AuthGuard } from './core/guards/auth.guard';
import { AppComponent } from './app.component';
import { NavBarComponent } from './core/nav-bar/nav-bar.component';

const routes: Routes = [
     {path: 'home', component: HomeComponent},
   {
    path: 'home',
    canActivate: [AuthGuard],
    loadChildren: () => import('./home/home.module').then(m => m.HomeModule)
   },
   { path: 'home', component: HomeComponent, canActivate: [AuthGuard] }, // Home’a gitmek için login olmalı
  { path: 'login', component: LoginComponent },
  { path: '', redirectTo: '/login', pathMatch: 'full' }, // Ana sayfa login sayfasına yönlendirir
  { path: '**', redirectTo: '/login' }

  //  {path: 'home', component: HomeComponent},
  //  {path: 'admin/dashboard', canActivate: [AuthGuard], component: DashboardComponent},
  //  {
  //   path: 'dashboard',
  //   canActivate: [AuthGuard],
  //   loadChildren: () => import('./admin/dashboard/dashboard.module').then(m => m.DashboardModule)
  // },
  //  {path: 'account/login', component: LoginComponent},
  // {path: 'account/register', component: RegisterComponent},
  { path: 'admin', loadChildren: () => import('./admin/admin.module').then(m => m.AdminModule) }, // Admin modülü
  {path: 'account', loadChildren: () => import('./account/account.module').then(m => m.AccountModule)},
  { path: '**', redirectTo: '' , pathMatch: 'full' }, // Hatalı bir rota girilirse
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule {}
