import { Component } from '@angular/core';
import { AbstractControl, FormGroup, FormBuilder, Validators } from '@angular/forms';
import { AccountService } from '../account.service';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  loginForm: FormGroup;
  errorMessage: string | null = null;
  loginResponse$: Observable<any> | null = null;
  constructor(private fb: FormBuilder, private accountService: AccountService, private router: Router) {
    this.loginForm = this.fb.group({
      userName: ['', Validators.required], // UserName input field
      password: ['', [Validators.required]], // Password input field
    });
  }

  get userName(): AbstractControl {
    return this.loginForm.get('userName')!;
  }

  get password(): AbstractControl {
    return this.loginForm.get('password')!;
  }

  onSubmit(): void {
    if (this.loginForm.invalid) {
      console.error('Form is invalid');
      return;
    }

    const loginData = {
      userName: this.loginForm.get('userName')?.value,
      password: this.loginForm.get('password')?.value,
    };

    // Login işlemi başlatılır
    // this.loginResponse$ = this.accountService.login(loginData); 

    // // Observable'ı subscribe ederek, yanıtı kontrol et
    // this.loginResponse$.subscribe({
    //   next: (response) => {
    //     console.log('Login başarılı:', response);
    //     if (response.success ) {
    //       // Başarılı login sonrası yönlendirme
    //       this.router.navigate(['/home']);
    //     } else {
    //       this.errorMessage = 'Login başarısız';
    //     }
    //   },
    //   error: (error) => {
    //     console.error('Login başarısız:', error);
    //     this.errorMessage = 'Bir hata oluştu';
    //   }
    // });
    this.accountService.login(loginData).subscribe({
      next: (response) => {
        console.log('Login yanıtı:', response);
        if (response.success) {
          this.accountService.setRole(response.role); // Rolü kaydediyoruz
          const userRole = response.role; // Rolü tekrar kontrol edelim
          if (userRole === 'Admin' || userRole === 'Client') {
            this.router.navigate(['/home']); // Doğru sayfaya yönlendirme
          } else {
            this.errorMessage = 'Unauthorized role';
            this.router.navigate(['/account/login']);
          }
        } else {
          this.errorMessage = 'Invalid username or password';
        }
      },
      error: (error) => {
        console.error('Login failed:', error);
        this.errorMessage = 'An error occurred during login';
      }
    });

 }

}
