import { Component, OnInit } from '@angular/core';
import { AccountService } from '../account.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent implements OnInit {
  model: any = {};

  constructor(private accountService: AccountService, private router: Router) {}
  ngOnInit(): void {
    this. register(); 
  }

  register() {
    console.log('Model being sent:', this.model);
    this.accountService.register(this.model).subscribe({
      next: () => this.router.navigateByUrl('/account/login'),
      error: error => {
        console.log('Error occurred:', error);
        alert('Registration failed. Please check the data and try again.');
      }
    });
  }

}
<div class="background-image">
    <div class="overlay"></div>
    <div class="content">

        <form (ngSubmit)="register()">
            <div class="form-group  my-3">
                <input class="form-control"  type="text" [(ngModel)]="model.displayName" name="displayName"   placeholder="İstifadəçi adı daxil edin:">
            </div>
            <div class="form-group  my-3">
                <input class="form-control"  type="text" [(ngModel)]="model.email" name="email"    placeholder="Emailinizi daxil edin:">
            </div>
            <div class="form-group  my-3">
                <input class="form-control"  type="text" [(ngModel)]="model.name" name="name"    placeholder="Adınızı daxil edin:">
            </div>
            <div class="form-group  my-3">
                <input class="form-control"  type="text" [(ngModel)]="model.surname" name="surname" placeholder="Soyadınızı daxil edin:">
            </div>
            <div class="form-group  my-3">
                <input class="form-control"  type="date" [(ngModel)]="model.birthDate" name="birthDate"  placeholder="Doğum tarixinizi daxil edin:">
            </div>
            <div class=" form-group my-3">
                <input class="form-control" type="password" [(ngModel)]="model.password" name="password"   placeholder="Şifrə daxil edin:">
            </div>
            <div class=" form-group my-3">
                <input class="form-control" type="password"  [(ngModel)]="model.confirmPassword" name="confirmPassword"  placeholder="Eyni şifrəni daxil edin:">
            </div>


            <button type="submit" class="btn btn-primary"> Qeydiyyatdan keç</button>
        </form>
    </div>
</div>
