import { Component, OnInit } from '@angular/core';
import { BookService } from '../book.service';
import { TypeService } from '../../type/type.service';
import { AuthorService } from '../../author/author.service';
import { Book } from 'src/app/shared/models/book';
import { Router } from '@angular/router';
import { HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-book-add',
  templateUrl: './book-add.component.html',
  styleUrls: ['./book-add.component.scss']
})
export class BookAddComponent implements OnInit{
  types: any[] = [];
  categories: any[] = [];
  authors: any[] = [];
  courses: any[] = [];
  book = {
    title: '',
    description: '',
    createBook: '',
    typeId: 0,
    categoryId: 0,
    authorId: 0,
    courseId: 0,
    photoFile: null,
    documentFile: null
  };
  constructor(private bookService: BookService, private router: Router){}

  ngOnInit(): void {
    this.loadTypes();
    this.loadCategories();
    this.loadAuthors();
    this.loadCourses();
    this.addBook();
  }

    loadTypes() {
      this.bookService.getTypes().subscribe({
       next: (response: any) => {
          console.log('API Response type:', response);
            this.types = response;
        },
          error: error => console.log('Error fetching Types:', error)
        })
    }
    loadAuthors() {
      this.bookService.getAuthors().subscribe({
       next: (response: any) => {
          console.log('API Response author:', response);
            this.authors = response;
        },
          error: error => console.log('Error fetching Authors:', error)
        })
    }
    loadCategories() {
      this.bookService.getCategories().subscribe({
        next: ( response : any) => {
          console.log('API Response category:', response);
          this.categories = response;
        },
        error: error=> console.log('Error fetching Categories:', error)

      })
    }
    loadCourses(){
      this.bookService.getCourses().subscribe({
        next: (response: any) => {
          console.log('API Response courses:', response);
          this.courses = response;
        },
        error: error=> console.log('Error fetching Courses:', error)
      })
    }

    onFileChange(event: any, fileType: 'photo' | 'document') {
      const file = event.target.files[0];
      if (fileType === 'photo') {
        this.book.photoFile = file;
      } else if (fileType === 'document') {
        this.book.documentFile = file;
      }
    }

    addBook() {
      const formData = new FormData();
      formData.append('Title', this.book.title);
      formData.append('Description', this.book.description);
      formData.append('CreateBook', this.book.createBook);
      formData.append('TypeId', this.book.typeId.toString());
      formData.append('CategoryId', this.book.categoryId.toString());
      formData.append('AuthorId', this.book.authorId.toString());
      // CourseId yalnızca geçerli bir değer varsa ekleniyor
      if (this.book.courseId && this.book.courseId > 0) {
        formData.append('CourseId', this.book.courseId.toString());
      }
      if (this.book.photoFile) {
        formData.append('PhotoFile', this.book.photoFile);
      }
      if (this.book.documentFile) {
        formData.append('DocumentFile', this.book.documentFile);
      }
      const token = localStorage.getItem('token');
      const headers = new HttpHeaders({
        'Authorization': `Bearer ${token}`
      });

      this.bookService.addBook(formData, headers).subscribe({
        next: (response) => {
          console.log('Book added successfully:', response);
          this.router.navigate(['/admin/book/book-list']);
        },
        error: (err) => {
          console.error('Error adding book:', err);

          if (err.error && err.error.includes('Eyni adlı kitab artıq mövcuddur')) {
            alert('Bu adda kitab artıq mövcuddur. Fərqli başlıq seçin.');
          }else if(err.error && err.error.includes('Invalid photo type. Only PNG, JPG, and GIF are allowed.')) {
            alert('Foto yerinə Sənəd faylı seçilə bilməz.');
          }else if(err.error && err.error.includes('Invalid document type. Only PDF are allowed.')) {
            alert('Sənəd yerinə Foto faylı seçilə bilməz.');
          } else {
            alert('Kitabı əlavə edərkən gözlənilməz xəta baş verdi.');
          }
        }
      });
    }

}
<div class="container mt-1">
  <div class="card shadow">
    <div class="card-header bg-primary text-white">
      <h2 class="text-center mb-0">Yeni Kitab Yarat</h2>
    </div>
    <div class="card-body">
      <form (ngSubmit)="addBook()" #bookForm="ngForm">
        <div class="mb-3">
          <label for="title" class="form-label">Title</label>
          <input
            id="title"
            type="text"
            class="form-control"
            [(ngModel)]="book.title"
            name="title"
            placeholder="Enter book title"
            required
            #title="ngModel"
          />
          <div *ngIf="title.touched && title.invalid" class="text-danger">
            Titulu yazmağınız tələb olunur.
          </div>
        </div>

        <div class="mb-1">
          <label for="description" class="form-label">Description</label>
          <textarea
              id="description"
              class="form-control"
              [(ngModel)]="book.description"
              name="description"
              rows="3"
              placeholder="Enter book description"
              required
              #description="ngModel"
          ></textarea>
          <div *ngIf="description.touched && description.invalid" class="text-danger">
            Məlumatı yazmağınız tələb olunur.
          </div>
        </div>

        <div class="mb-1">
          <label for="createBook" class="form-label">Müddəti</label>
          <input
            type="date"
            id="createBook"
            class="form-control"
            [(ngModel)]="book.createBook"
            name="createBook"
            placeholder="Enter book date"
            #createBook="ngModel"
            required
          />
          <div *ngIf="createBook.touched && createBook.invalid" class="text-danger">
          Kitabın müddətin yazmağınız tələb olunur.
          </div>
        </div>

        <div class="mb-1">
          <label for="type" class="form-label">Tipi</label>
          <select
            id="type"
            class="form-select"
            [(ngModel)]="book.typeId"
            name="type"
            required
            #type="ngModel"
          >
          <option value="0">Tipin seçin</option>
            <option *ngFor="let type of types" [value]="type.id">
              {{ type.typeName }}
            </option>
          </select>
          <div *ngIf="type.touched && type.invalid" class="text-danger">
            Tipin yazmağınız tələb olunur.
          </div>
        </div>

        <div class="mb-1">
          <label for="category" class="form-label">Kateqoriyası</label>
          <select
            id="category"
            class="form-select"
            [(ngModel)]="book.categoryId"
            name="category"
            required
            #category="ngModel"
          >
          <option value="0">Kateqoriyasın seçin</option>
            <option *ngFor="let category of categories" [value]="category.id">
              {{ category.name }}
            </option>
          </select>
          <div *ngIf="category.touched && category.invalid" class="text-danger">
            Kateqoriyasın yazmağınız tələb olunur.
          </div>
        </div>

        <div class="mb-1">
          <label for="author" class="form-label">Müəllifi</label>
          <select
            id="author"
            class="form-select"
            [(ngModel)]="book.authorId"
            name="author"
            required
            #author="ngModel"
          >
            <option value="0">Müəllifin seçin</option>
            <option *ngFor="let author of authors" [value]="author.id">
              {{ author.fullName }}
            </option>
          </select>
          <div *ngIf="author.touched && author.invalid" class="text-danger">
           Müəllifin yazmağınız tələb olunur.
          </div>
        </div>

        <div class="mb-1">
          <label for="course" class="form-label">Kurslar</label>
          <select
            id="course"
            class="form-select"
            [(ngModel)]="book.courseId"
            name="course"
            required
            #course="ngModel"
          >
            <option value="0">Kursu seçin</option>
            <option *ngFor="let course of courses" [value]="course.id">
              {{ course.title}} 
            </option>
          </select>
          <div *ngIf="author.touched && author.invalid" class="text-danger">
           Kursu yazmağınız tələb olunur.
          </div>
        </div>

        <div class="mb-1">
          <label for="photo" class="form-label">Foto</label>
          <input
            id="photo"
            type="file"
            class="form-control"
            (change)="onFileChange($event, 'photo')"
            placeholder="Choose a photo file"
          />
          <div *ngIf="!book.photoFile" class="invalid-feedback">
            Bir şəkil seçin.
          </div>
        </div>

        <div class="mb-1">
          <label for="document" class="form-label">Fayl</label>
          <input
            id="document"
            type="file"
            class="form-control"
            (change)="onFileChange($event, 'document')"
            placeholder="Choose a document file"
          />
          <div *ngIf="!book.documentFile" class="invalid-feedback">
            Bir fayl seçin.
          </div>
        </div>

        <div class="d-grid">
          <button type="submit" class="btn btn-primary btn-block" [disabled]="bookForm.invalid">
            Kitabı daxil et
          </button>
        </div>
      </form>
    </div>
  </div>
</div>
