import { Component, OnInit } from '@angular/core';
import {Book} from 'src/app/shared/models/book'; 
import { BookService } from '../book.service';
import { ActivatedRoute } from '@angular/router';
import { BookParams } from 'src/app/shared/models/bookParams';
import { TypeService } from '../../type/type.service';
import { AuthorService } from '../../author/author.service';
@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.scss']
})
export class BookListComponent implements OnInit{

    books: any[] = [];
    types: string[] = [];
    categories: string[] = [];
    authors: string[] = [];
    totalRecords: number = 0;

    bookParams = {
      search: '',
      typeId: '',
      categoryId: '',
      authorId: '',
      pageNumber: 1,
      pageSize: 10,
    };

    constructor(
      private bookService: BookService,
      private typeService: TypeService,
      // private categoryService: CategoryService,
      private authorService: AuthorService
    ) {}

    ngOnInit(): void {
      this.getBooks();
      this.getTypes();
      // this.getCategories();
      this.getAuthors();
    }

    getBooks(): void {
      this.bookService.getBooks(this.bookParams).subscribe(
        (response) => {
          this.books = response.books;
          this.totalRecords = response.totalCount;
        },
        (error) => {
          console.error('Error fetching books:', error);
        }
      );
    }

    getTypes(): void {
      this.typeService.getTypes().subscribe((types) => (this.types = types));
    }

    // getCategories(): void {
    //   this.categoryService.getCategories().subscribe((categories) => (this.categories = categories));
    // }

    getAuthors(): void {
      this.authorService.getAuthors().subscribe((authors) => (this.authors = authors));
    }

    onSearch(search: string): void {
      this.bookParams.search = search;
      this.bookParams.pageNumber = 1;
      this.getBooks();
    }

    applyFilters(): void {
      this.bookParams.pageNumber = 1;
      this.getBooks();
    }

    changePage(pageNumber: number): void {
      if (pageNumber > 0) {
        this.bookParams.pageNumber = pageNumber;
        this.getBooks();
      }
    }
  }
<input type="text" placeholder="Search..." (input)="onSearch($event.target?.value)" />

<select [(ngModel)]="bookParams.typeId" (change)="applyFilters()">
  <option value="">All Types</option>
  <option *ngFor="let type of types" [value]="type">{{ type }}</option>
</select>

<select [(ngModel)]="bookParams.categoryId" (change)="applyFilters()">
  <option value="">All Categories</option>
  <option *ngFor="let category of categories" [value]="category">{{ category }}</option>
</select>

<select [(ngModel)]="bookParams.authorId" (change)="applyFilters()">
  <option value="">All Authors</option>
  <option *ngFor="let author of authors" [value]="author">{{ author }}</option>
</select>

<table>
  <thead>
    <tr>
      <th>Title</th>
      <th>Author</th>
      <th>Category</th>
      <th>Type</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let book of books">
      <td>{{ book.title }}</td>
      <td>{{ book.bookAuthor }}</td>
      <td>{{ book.bookCategory }}</td>
      <td>{{ book.bookType }}</td>
    </tr>
  </tbody>
</table>

<div class="pagination">
  <button (click)="changePage(bookParams.pageNumber - 1)" [disabled]="bookParams.pageNumber === 1">Previous</button>
  <span>Page {{ bookParams.pageNumber }}</span>
  <button (click)="changePage(bookParams.pageNumber + 1)" [disabled]="books.length < bookParams.pageSize">Next</button>
</div>
