import { Component, OnInit} from '@angular/core';
import {Book} from 'src/app/shared/models/book'; 
import { BookService } from '../book.service';
import { ActivatedRoute } from '@angular/router';
import { BookParams } from 'src/app/shared/models/bookParams';
import { TypeService } from '../../type/type.service';
import { AuthorService } from '../../author/author.service';
import { Author } from 'src/app/shared/models/author';
import { Category } from 'src/app/shared/models/category';
import { Type } from 'src/app/shared/models/type';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.scss']
})
export class BookListComponent implements OnInit{
  Math = Math;
  books: any[] = []; 
  totalRecords: number = 0;
  categories: any[] = [];
  types: any[] = []; 
  authors: any[] = []; 
  searchForm: FormGroup;

  constructor(private bookService: BookService, private fb: FormBuilder) {
    // Initialize the reactive form with default values
    this.searchForm = this.fb.group({
      search: [''],
      typeId: [''],
      categoryId: [''],
      authorId: [''],
      pageNumber: [1],
      pageSize: [10]
    });
  }

  ngOnInit(): void {
    this.getFilters(); 
    this.getBooks(); 
  }

  getBooks(): void {

    const params = this.searchForm.value; // Get current form values as parameters
    this.bookService.getBooks(params).subscribe(response => {
      if (response.books && response.books.$values) {
        this.books = response.books.$values; // Doğrudan $values dizisini al
        this.totalRecords = response.totalRecords; // Toplam kayıt sayısını güncelle
      } 
    });
    this.bookService.getCategories().subscribe({
      next: (data) => {
        this.categories = data;
        console.log('Categories fetched successfully:', data);
      },
      error: (err) => {
        console.error('Error fetching categories:', err);
      },
    });
  }


  // Fetch filter data (categories, types, authors) from the API using the service
  getFilters(): void {
    this.bookService.getCategories().subscribe(data => this.categories = data);
    this.bookService.getTypes().subscribe(data => this.types = data);
    this.bookService.getAuthors().subscribe(data => this.authors = data);
  }

  // Handle page change for pagination
  onPageChange(page: number): void {
    this.searchForm.patchValue({ pageNumber: page }); // Update the current page number
    this.getBooks(); // Fetch books for the new page
  }

  // Handle form submission for search and filter
  onSearch(): void {
    console.log('Search Params:', this.searchForm.value);
    this.searchForm.patchValue({ pageNumber: 1 });
    this.getBooks();
  }

