import { Component, OnInit } from '@angular/core';
import { SlideService } from '../slide.service';

@Component({
  selector: 'app-slide-edit',
  templateUrl: './slide-edit.component.html',
  styleUrls: ['./slide-edit.component.scss']
})
export class SlideEditComponent implements OnInit {
  slide = {
    id: 0,
    photo: null
  }
  constructor(private slideService: SlideService, private route: ActivatedRoute){}
  ngOnInit(): void {
    this.EditSlide();
  }
  onFileChange(event: any, fileType: 'photo' ) {
    const file = event.target.files[0];
    if (fileType === 'photo') {
      this.slide.photo = file;
    } 
  }
  EditSlide(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    const formData = new FormData();
    this.slideService.updateSlide(id , formData).subscribe({

    });
  }

}
