import { Component, OnInit } from '@angular/core';
import { TypeService } from '../type.service';

@Component({
  selector: 'app-type-add',
  templateUrl: './type-add.component.html',
  styleUrls: ['./type-add.component.scss']
})
export class TypeAddComponent implements OnInit {
  type = {
    typeName:''
  };
  constructor(private typeService: TypeService){}
  ngOnInit(): void {
    this.AddType();
  }
  AddType(){
    const formData = new FormData();
    formData.append('typeName', this.type.typeName);


    this.typeService.addType(formData).subscribe({
      next:(response: any) => {
        formData.append()
      }
    })
  }

  addBook() {
    const formData = new FormData();
    formData.append('Title', this.book.title);
    formData.append('Description', this.book.description);
    formData.append('CreateBook', this.book.createBook);
    formData.append('TypeId', this.book.typeId.toString());
    formData.append('CategoryId', this.book.categoryId.toString());
    formData.append('AuthorId', this.book.authorId.toString());
    if (this.book.photoFile) {
      formData.append('PhotoFile', this.book.photoFile);
    }
    if (this.book.documentFile) {
      formData.append('DocumentFile', this.book.documentFile);
    }

    this.bookService.addBook(formData).subscribe({
      next: (response) => {
        console.log('Book added successfully:', response);
        this.router.navigate(['/admin/book/book-list']); // Başarılı ekleme sonrası yönlendirme
      },
      error: (err) => {
        console.error('Error adding book:', err);
      }
    });
  }
}
