import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserService } from '../user.service';

@Component({
  selector: 'app-user-add',
  templateUrl: './user-add.component.html',
  styleUrls: ['./user-add.component.scss']
})
export class UserAddComponent {
  registerForm: FormGroup;
  errorMessage: string | null = null;
  availableRoles = ['Admin', 'Client']; // Mevcut roller

  constructor(private fb: FormBuilder, private userService: UserService, private router: Router) {
    this.registerForm = this.fb.group({
      userName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      name: ['', Validators.required],
      surname: ['', Validators.required],
      birthDate: ['', Validators.required],
      password: [
        '',
        [
          Validators.required,
          Validators.minLength(6),
          Validators.pattern('(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)(?=.*[!@#$%^&*()_+}{":;\',.<>?/]).*'),
        ],
      ],
      confirmPassword: ['', Validators.required],
      roles: [[]], // Roller için form alanı
    });
  }

  onRoleSelect(event: any): void {
    const selectedOptions = Array.from(event.target.selectedOptions).map(
      (option: any) => option.value
    );
    this.registerForm.patchValue({ roles: selectedOptions });
  }

  onSubmit(): void {
    if (this.registerForm.invalid) {
      console.error('Form is invalid');
      return;
    }

    // RegisterDto nesnesini oluştur
    const registerDto = {
      UserName: this.registerForm.get('userName')?.value,
      Email: this.registerForm.get('email')?.value,
      Name: this.registerForm.get('name')?.value,
      Surname: this.registerForm.get('surname')?.value,
      BirthDate: this.registerForm.get('birthDate')?.value,
      Password: this.registerForm.get('password')?.value,
      ConfirmPassword: this.registerForm.get('confirmPassword')?.value,
    };

    // RoleDto nesnesini oluştur
    const roleDto = {
      Roles: this.registerForm.get('roles')?.value,
    };

    // API'ye gönderilecek payload
    const payload = {
      RegisterDto: {
        UserName: this.registerForm.get('userName')?.value,
        Email: this.registerForm.get('email')?.value,
        Name: this.registerForm.get('name')?.value,
        Surname: this.registerForm.get('surname')?.value,
        BirthDate: this.registerForm.get('birthDate')?.value,
        Password: this.registerForm.get('password')?.value,
        ConfirmPassword: this.registerForm.get('confirmPassword')?.value,
      },
      RoleDto: {
        Roles: this.registerForm.get('roles')?.value,
      },
    };


    // JSON formatında payload gönderimi
    this.userService.getRegisterRole(payload).subscribe({
      next: (response) => {
        console.log('Registration successful:', response);
        this.router.navigate(['admin/user/user-list']);
      },
      error: (error) => {
        console.error('Registration failed:', error);
        this.errorMessage = 'Registration failed. Please try again later.';
      },
    });
  }
}
<form [formGroup]="registerForm" (ngSubmit)="onSubmit()" class="register-form container p-4 shadow-lg rounded bg-light">
  <h2 class="text-center mb-4">Qeydiyyat</h2>

  <div class="mb-3">
    <label for="userName" class="form-label">İstifadəçi adı:</label>
    <input
      type="text"
      id="userName"
      formControlName="userName"
      class="form-control"
      placeholder="Enter your username"
      required
    />
  </div>

  <div class="mb-3">
    <label for="email" class="form-label">Email:</label>
    <input
      type="email"
      id="email"
      formControlName="email"
      class="form-control"
      placeholder="Enter your email"
      required
    />
  </div>

  <div class="mb-3">
    <label for="name" class="form-label">Adı:</label>
    <input
      type="text"
      id="name"
      formControlName="name"
      class="form-control"
      placeholder="Enter your name"
      required
    />
  </div>

  <div class="mb-3">
    <label for="surname" class="form-label">Soyadı:</label>
    <input
      type="text"
      id="surname"
      formControlName="surname"
      class="form-control"
      placeholder="Enter your surname"
      required
    />
  </div>

  <div class="mb-3">
    <label for="birthDate" class="form-label">Doğum tarixi:</label>
    <input
      type="date"
      id="birthDate"
      formControlName="birthDate"
      class="form-control"
      required
    />
  </div>

  <div class="mb-3">
    <label for="password" class="form-label">Şifrə:</label>
    <input
      type="password"
      id="password"
      formControlName="password"
      class="form-control"
      placeholder="Enter your password"
      required
    />
  </div>

  <div class="mb-3">
    <label for="confirmPassword" class="form-label">Yenidən şifrə:</label>
    <input
      type="password"
      id="confirmPassword"
      formControlName="confirmPassword"
      class="form-control"
      placeholder="Enter your password"
      required
    />
  </div>

  <div class="mb-3">
    <label for="roles" class="form-label">Rollar:</label>
    <select id="roles" multiple class="form-select" (change)="onRoleSelect($event)">
      <option *ngFor="let role of availableRoles" [value]="role">{{ role }}</option>
    </select>
  </div>

  <button type="submit" class="btn btn-primary w-100">Qeydiyyat</button>
</form>
