import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { UserService } from '../user.service';

@Component({
  selector: 'app-change-role',
  templateUrl: './change-role.component.html',
  styleUrls: ['./change-role.component.scss']
})
export class ChangeRoleComponent implements OnInit {
  changeRoleForm: FormGroup; 
  availableRoles: string[] = [];
  userId: string | null = null;
  errorMessage: string | null = null;

  constructor(
    private fb: FormBuilder,
    private route: ActivatedRoute,  // ActivatedRoute ile URL parametrelerini alacağız
    private userService: UserService  // API çağrısı için servis
  ) {
    this.changeRoleForm = this.fb.group({
      roles: [[], Validators.required]  // Kullanıcının seçebileceği roller
    });
  }

  ngOnInit(): void {
    // URL'den userId'yi alıyoruz
    this.route.paramMap.subscribe(params => {
      this.userId = params.get('userId');  // URL'deki parametreyi alıyoruz
      if (this.userId) {
        this.loadRoles();  // Kullanıcı ID'si varsa, roller alınacak
      }
    });
  }

  // Rolleri API'den alıyoruz
  loadRoles(): void {
    this.userService.getAvailableRoles().subscribe(
      (roles) => {
        this.availableRoles = roles;  // Roller dizisini güncelliyoruz
      },
      (error) => {
        this.errorMessage = 'Unable to load roles.';
      }
    );
  }

  // Formu submit ettiğimizde rol güncellemesi yapıyoruz
  onSubmit(): void {
    if (this.changeRoleForm.invalid) {
      return;
    }

    const formData = this.changeRoleForm.value;
    if (this.userId) {
      this.userService.changeUserRole(this.userId, formData.roles).subscribe(
        (response) => {
          alert(response.message);  // Başarılı olursa kullanıcıya bildirim
        },
        (error) => {
          this.errorMessage = 'Unable to update role.';
        }
      );
    }
  }
}
